-- Get List of all Realm Names
GRM.GetRealmNames = function()
    if not initialized then
        InitializeRealms()
    end

    local region = string.lower(GetCurrentRegionName())
    if region == "cn" then
        region = "us"
    end

    return Realms[region][GetLocale()]
end