            end);
        
            GRM_UI.MemberDetailFrameClassic:SetScript ( "OnHide" , function()
                if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].showMouseoverOld and not GRM_G.CurrentPinCommunity then
