-- Method:          GRM.LoadSettings()
-- What it Does:    On first time loading addon, it builds default addon settings. It checks for addon version change
--                  And, if there are any changes, they will be added into that logic block. 
--                  And new setting can be tagged on.
-- Purpose:         Saving settings between gaming sessions. Also, this is built to provide backwards compatibility for future flexibility on feature adding, if necessary.
GRM.LoadSettings = function()

    if GRM_AddonSettings_Save["H"] == nil and GRM_AddonSettings_Save[1] == nil then
        GRM_GuildMemberHistory_Save , GRM_PlayersThatLeftHistory_Save , GRM_CalendarAddQue_Save , GRM_LogReport_Save , GRM_AddonSettings_Save , GRM_PlayerListOfAlts_Save , GRM_GuildDataBackup_Save = GRM.ClearPermData();
    end

    -- Build the settings
    -- Need to doublecheck Faction Index ID
    if GRM_G.FID == 0 then
        if GRM_G.faction == "Horde" then
            GRM_G.FID = 1;
            GRM_G.selectedFID = "H";
            GRM_G.F = "H";
        else
            GRM_G.FID = 2;
            GRM_G.selectedFID = "A";
            GRM_G.F = "A";
        end
    end

    if GRM_G.F == "" then
        if GRM_G.faction == "Horde" then
            GRM_G.F = "H";
            GRM_G.selectedFID = "H";
        else
            GRM_G.F = "A";
            GRM_G.selectedFID = "A";
        end
    end

    local patchSettingsIsNeeded = false;
    local isFound = false;
    local indexFound = 0;
    local indexFoundFaction = "";

    -- This means we are in the old database that needs to be updated.
    -- Pre 1.84
    if GRM_AddonSettings_Save["H"] == nil then
        GRM_G.oldDB = true;
        for j = 2 , #GRM_AddonSettings_Save[GRM_G.FID] do
            if GRM_AddonSettings_Save[GRM_G.FID][j][1] == GRM_G.addonUser then
                isFound = true;
                indexFound = j;
                indexFoundFaction = GRM_G.FID;

                if GRM_AddonSettings_Save[GRM_G.FID][j][2][1] ~= GRM_G.Version then
                    patchSettingsIsNeeded = true;
                end
                break;
            end
        end
        
        -- Verify at least one profile needs to be updated. This is done to account for new toons as well on an update.
        if not isFound then
            for i = 1 , #GRM_AddonSettings_Save do
                for j = 2 , #GRM_AddonSettings_Save[i] do
                    if GRM_AddonSettings_Save[i][j][2][1] ~= GRM_G.Version then
                        patchSettingsIsNeeded = true;
                        indexFound = j;
                        indexFoundFaction = i;
                        break;
                    end
                end
                if patchSettingsIsNeeded then
                    break;
                end
            end

        -- Integrity check on the addon settings.
        else
            isFound = GRM_Patch.PlayerSettingsIntegrityCheck();
        end
        
    else

        -- 1.84 and on
        if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] ~= nil then
            isFound = true;
            indexFoundFaction = GRM_G.F;
            indexFound = GRM_G.addonUser;     -- Maintain formatting compatibility with old indexing for ease.
        end

        for x in pairs ( GRM_AddonSettings_Save ) do
            for y in pairs ( GRM_AddonSettings_Save[x] ) do
                if GRM_AddonSettings_Save[x][y].version ~= nil and GRM_AddonSettings_Save[x][y].version ~= GRM_G.Version then
                    patchSettingsIsNeeded = true;
                    indexFound = y;
                    indexFoundFaction = x;
                    break;
                end
            end
            if patchSettingsIsNeeded then
                break;
            end
        end
        
    end

    -- Build settings for first time.
    if not isFound then
        GRM.Report ( "\n" .. GRM.L ( "Configuring Guild Roster Manager for {name} for the first time." , GetUnitName ( "PLAYER" , false ) ) );

        if not GRM_G.oldDB then

            -- Add new player
            GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] = {};
            -- Load the Default Settings
            for i = 0 , GRM_G.SettingsPages do
                GRM.SetDefaultAddonSettings ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] , i , false );
            end

            GRM.ConfigureMiscForPlayer( GRM_G.addonUser );

        else
            -- Old Database so still have FID outdated system
            -- Add new player
            table.insert ( GRM_AddonSettings_Save[GRM_G.FID] , { GRM_G.addonUser } );
            GRM_G.setPID = #GRM_AddonSettings_Save[GRM_G.FID];                                -- We know what the ID is...
            

            local AllDefaultSettings = GRM_Patch.GetDefaultAddonSettings();
            -- Unique Settings added to the player.
            table.insert ( GRM_AddonSettings_Save[GRM_G.FID][GRM_G.setPID] , AllDefaultSettings );
        end

        GRM_G.IsNewToon = true;
        -- Forcing core log window/options frame to load on the first load ever as well
        GRM_G.ChangesFoundOnLoad = true;
    elseif not IsInGuild() then
        GRM_G.IsNewToon = true;
        GRM_G.ChangesFoundOnLoad = true;
    end

    if patchSettingsIsNeeded then
        -- numericV is used to compare older versions.
        local playerV;
        if not GRM_G.oldDB then
            playerV = GRM_AddonSettings_Save[indexFoundFaction][indexFound].version;
        else
            playerV = GRM_AddonSettings_Save[indexFoundFaction][indexFound][2][1];
        end

        -- PATCH FIXES
        GRM_Patch.SettingsCheck ( tonumber ( string.match ( playerV , "R(.+)" ) ) );
        
    else
        -- No need to delay
        GRM.FinalSettingsConfigurations();
    end

end

-- Method:          GRM.FinalSettingsConfigurations()
-- What it Does:    Calculates the final settings configurations
-- Purpose:         Compartmentalizes this so it can only be on call as needed.
GRM.FinalSettingsConfigurations = function()

    if GRM_G.oldDB then
        GRM.ConfigureMiscForPlayer( GRM_G.addonUser );
    end

    -- In case of disconnect during some events that you may need to continue from.
    GRM.MiscCleanupOnLogin();
    -- Let's load that minimap button now too...
    GRM_UI.GRM_MinimapButtonInit();
    -- One time processing, saves a bit of resources for an oft used string manipulation feature.
    GRM_UI.CorePositionInit();
    GRM_UI.CoreToolPositionInit();
    -- Register window to save data to.
    GRM.SetReportWindow( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].reportChannel );
    -- Set Window Scales
    GRM.SetAllWindowScales();

    GRM.RefreshMainTagHexCode();

    if IsInGuild() then
        GRM.SetGuildInfoDetails(); -- an early systems check too
    end

    -- Re-setup macro
    if GRM_G.BuildVersion < 40000 then
        GRM.BuildGuildRosterHotkeyAndMacroCLASSIC();
    else
        GRM.BuildGuildRosterHotkeyAndMacro();
    end

    -- For sync...
    GRMsyncGlobals.timeAtLogin = time();    -- Important for Sync Leader backend election algorithm.
    GRMsyncGlobals.ThrottleCap = GRMsyncGlobals.ThrottleCap * GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncSpeed;   -- Adjust the throttle cap

    -- The Tag Headers
    GRM.SetJoinAndRejoinTags();

    -- General one-time configurations
    GRM.RefreshNumberOfHoursTilRecommend();

    -- Classic Chat coloring
    GRM.SetClassChatColoring()

    GRM_G.AddonIsFullyConfigured = true;
    GRM_API.Initialized = true;

    -- Settings loaded... carry on.
    GRM.SettingsLoadedFinishDataLoad();

    
end

-------------------------------------
-------- BUILD COMPATIBILITY --------
-------------------------------------

-- ALL these functions are to deal with possibility of Retail and Classic backend APIs no longer being sync'd the same. When 9.0 launched October 2020 the underlying API was changed for many things, but Classic did not receive those updates. It is assumed those will eventually be made the same again in future updates for Classic. By Wrapping them in a GRM function it can be easy to control by just needing to update them here rather than addon-wide.

GRM.CanViewOfficerNote = function()
    if C_GuildInfo.CanViewOfficerNote then
        return C_GuildInfo.CanViewOfficerNote();
    else
        return CanViewOfficerNote();
    end
end

GRM.CanEditOfficerNote = function()
    if C_GuildInfo.CanEditOfficerNote then
        return C_GuildInfo.CanEditOfficerNote();
    else
        return CanEditOfficerNote();
    end
end

GRM.GuildRoster = function()
    if C_GuildInfo.GuildRoster then
        C_GuildInfo.GuildRoster();
    else
        GuildRoster();
    end
end

GRM.GetTodaysDate = function()
    local calendarTime;

    if C_DateAndTime.GetCurrentCalendarTime then
        calendarTime = C_DateAndTime.GetCurrentCalendarTime();
        return calendarTime.weekday, calendarTime.month, calendarTime.monthDay, calendarTime.year;
    else
        calendarTime = C_DateAndTime.GetTodaysDate();
        return calendarTime.weekDay, calendarTime.month, calendarTime.day, calendarTime.year;
    end
end

GRM.GetCurrentCalendarTime = function()
    if C_DateAndTime.GetCurrentCalendarTime then
        return C_DateAndTime.GetCurrentCalendarTime();
    else
        return C_DateAndTime.GetTodaysDate();
    end
end

---------------------------------------
----- END OF BUILD COMPAT... ----------
---------------------------------------

-- Method:          SetClassChatColoring()
-- What it Does:    Initializes chat coloring controls for classic
-- Purpose:         Quality of life control
GRM.SetClassChatColoring = function()
    if GRM_G.BuildVersion < 40000 then
        local num = 0;
        
        if not GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].colorizeClassicRosterNames then
            num = 1;
            if GetCVar ( "chatClassColorOverride" ) == "2" then
                num = 2;
            end
        end

        SetCVar("chatClassColorOverride" , num );

        hooksecurefunc ( "GuildStatus_Update" , function()
            local button;
            if IsInGuild() and GuildFrame and GuildFrame:IsVisible() then
                for i = 1 , 13 do

                    if GuildStatusFrame and GuildStatusFrame:IsVisible() then
                        button = _G["GuildFrameGuildStatusButton" .. i];
                        if button and button:IsVisible() then
                            GRM.RecolorText( button );
                        end
                    elseif GuildPlayerStatusFrame and GuildPlayerStatusFrame:IsVisible() then
                        button = _G["GuildFrameButton" .. i];
                        if button and button:IsVisible() then
                            GRM.RecolorText( button );
                        end
                    end
                end
            end
        end);

    end
end

-- method:          GRM.RefreshNumberOfHoursTilRecommend()
-- What it Does:    Rebuilds the time on the recommends for the macro tool and the log.
-- Purpose:         Resource saving. No need to process over and over everytime it is looked at unless a change is made.
GRM.RefreshNumberOfHoursTilRecommend = function()
    GRM_G.NumberOfHoursTilRecommend["kick"] = {};
    GRM_G.NumberOfHoursTilRecommend["kickActive"] = {};

    GRM_G.NumberOfHoursTilRecommend["promote"] = {};
    GRM_G.NumberOfHoursTilRecommend["demote"] = {};

    local validateFormat = function ( exactRule , defaultMonths )
        
        if type ( exactRule.numDaysOrMonths ) ~= "number" then
            exactRule.numDaysOrMonths = 12;
            exactRule.isMonths = true;
        end

        if not exactRule.rankSpecialNumDaysOrMonths then
            exactRule.rankSpecialNumDaysOrMonths = defaultMonths;
        end

        return exactRule;
    end

    for ruleName , rule in pairs ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].kickRules ) do
        
        if rule.activityFilter then                                               -- Only need to add if this part is enabled.
            GRM_G.NumberOfHoursTilRecommend["kick"][ruleName] = 0;

            rule = validateFormat ( rule , 12 );

            if rule.isMonths then
                GRM_G.NumberOfHoursTilRecommend["kick"][ruleName] = GRM.GetNumHoursTilRecommend ( rule.numDaysOrMonths );
            else
                GRM_G.NumberOfHoursTilRecommend["kick"][ruleName] = ( rule.numDaysOrMonths * 24 );
            end
        end
        if rule.applyEvenIfActiive then
            GRM_G.NumberOfHoursTilRecommend["kickActive"][ruleName] = 0;
            if rule.rankSpecialIsMonths then
                GRM_G.NumberOfHoursTilRecommend["kickActive"][ruleName] = GRM.GetNumHoursTilRecommend ( rule.rankSpecialNumDaysOrMonths );
            else
                GRM_G.NumberOfHoursTilRecommend["kickActive"][ruleName] = ( rule.rankSpecialNumDaysOrMonths * 24 );
            end
        end
    end

    for ruleName , rule in pairs ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].promoteRules ) do
        if rule.activityFilter then                                               -- Only need to add if this part is enabled.
            GRM_G.NumberOfHoursTilRecommend["promote"][ruleName] = {};
            GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].hours = 0;

            rule = validateFormat ( rule , 3 );

            if rule.isMonths then
                GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].hours = GRM.GetNumHoursTilRecommend ( rule.numDaysOrMonths );
            else
                GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].hours = ( rule.numDaysOrMonths * 24 );
            end

            if not rule.regardlessOfActivity then
                GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].inactive = 0;
                if rule.rankSpecialIsMonths then
                    GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].inactive = GRM.GetNumHoursTilRecommend ( rule.rankSpecialNumDaysOrMonths );
                else
                    GRM_G.NumberOfHoursTilRecommend["promote"][ruleName].inactive = ( rule.rankSpecialNumDaysOrMonths * 24 );
                end
            end

        end
    end

    for ruleName , rule in pairs ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].demoteRules ) do
        if rule.activityFilter then                                               -- Only need to add if this part is enabled.
            GRM_G.NumberOfHoursTilRecommend["demote"][ruleName] = {};
            GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].hours = 0;

            rule = validateFormat ( rule );

            if rule.isMonths then
                GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].hours = GRM.GetNumHoursTilRecommend ( rule.numDaysOrMonths );
            else
                GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].hours = ( rule.numDaysOrMonths * 24 );
            end

            if not rule.regardlessOfActivity then
                GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].evenIfActiveHours = 0;
                if rule.rankSpecialIsMonths then
                    GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].evenIfActiveHours = GRM.GetNumHoursTilRecommend ( rule.rankSpecialNumDaysOrMonths );
                else
                    GRM_G.NumberOfHoursTilRecommend["demote"][ruleName].evenIfActiveHours = ( rule.rankSpecialNumDaysOrMonths * 24 );
                end
            end

        end
    end
end

-- Method:          GRM.GetPageIndex()
-- What it Does:    Returns the index number to the corresponding frame
-- Purpose:         To coordinate in resetting default settings based on the specific frame the player is on.
GRM.GetPageIndex = function()
    local allFrames = { [ GRM_GeneralOptionsFrame ] = 1  , [ GRM_ScanningOptionsFrame ] = 2  , [ GRM_SyncOptionsFrame ] = 3  , [ GRM_OfficerOptionsFrame ] = 4  , [ GRM_UXOptionsFrame ] = 7 , [ GRM_LogExtraOptionsFrame ] = 8  , [GRM_ModulesFrame] = 15 };
    local result;

    for x , y in pairs ( allFrames ) do
        if x:IsVisible() then
            result = y;
        end
    end
    return result;
end

-- Method:          GRM.ResetDefaultSettings( int )
-- What it Does:    Resets the OPTIONS to the default one for only the currently logged in player
-- Purpose:         Easy, quality of life for user in the options, for simple reset.
GRM.ResetDefaultSettings = function( pageIndex )
    local page = pageIndex or GRM.GetPageIndex();
    local needsRefresh = false;
    local resetAll = false;

    if IsShiftKeyDown() and IsControlKeyDown() then
        needsRefresh = true;
        resetAll = true;

        -- resetting ALL player data.
        for i = 0 , GRM_G.SettingsPages do
            GRM.SetDefaultAddonSettings ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] , i , false );
        end

    elseif page then
        needsRefresh = true;
        -- Reset only specific settings
        GRM.SetDefaultAddonSettings ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] , page , false );

        if page == 10 then
            GRM.RefreshNumberOfHoursTilRecommend();
        end

    end

    if needsRefresh then

        if ( resetAll or page == 1 ) and GRM_UI.GRM_RosterChangeLogFrame:IsVisible() then
            GRML.SetNewLanguage( GRM_G.LocalizedIndex , false , true );
        end

        -- Check Permissions
        GRM.UpdateGuildLeaderPermissions ( false , true );

        -- Reset frames as needed
        if ( resetAll or page < 9 ) and GRM_UI.GRM_RosterChangeLogFrame:IsVisible() then     -- General options
            GRM_UI.BuildLogFrames();
        end

        if page == 15 then
            GRM_UI.ConfigureGroupInfoRules();
        end

        -- refresh the log as settings might be changed.
        if ( resetAll or page == 8 ) and GRM_UI.GRM_RosterChangeLogFrame.GRM_LogFrame:IsVisible() then
            GRM_UI.RefreshLogExtraOptions();
            GRM_UI.RefreshLogColorOptions();
            GRM.ResetLogStringPoints ( true );
            GRM.BuildLogComplete( true , false );
        end

        if ( resetAll or page == 9 ) and GRM_UI.GRM_RosterChangeLogFrame.GRM_ExportLogBorderFrame:IsVisible() then
            GRM_UI.SetExportTabHighlights ( GRM_UI.GRM_RosterChangeLogFrame.GRM_ExportLogBorderFrame.TabPosition );
        end

        if ( resetAll or ( page > 9 and page < 13 ) ) and GRM_UI.GRM_ToolCoreFrame:IsVisible() then
            GRM_UI.RefreshManagementTool( GRM_G.KickAltControl );
        end

        GRM_UI.GRM_MinimapButtonInit();
        GRM_UI.CorePositionInit();
        GRM_UI.CoreToolPositionInit();
        GRM.SetAllWindowScales();
        GRM.RefreshMainTagHexCode();
        GRM.SetReportWindow( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].reportChannel );

        GRM.RefreshNumberOfHoursTilRecommend();
    end

end

-- Method:          GRM.SyncAddonSettings()
-- What it Does:    It syncs all of the addon settings of the current player with all of the other alts the player has within that guild
-- Purpose:         To have a "global" settings option.
GRM.SyncAddonSettings = function()

    if GRM_G.guildName ~= "" and GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] ~= nil then
        for altName in pairs ( GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] ) do

            if altName ~= GRM_G.addonUser and GRM_AddonSettings_Save[GRM_G.F][altName] ~= nil then

                local tempSettingsHolder = GRM.DeepCopyArray ( { GRM_AddonSettings_Save[GRM_G.F][altName].minimapEnabled , GRM_AddonSettings_Save[GRM_G.F][altName].minimapPos , GRM_AddonSettings_Save[GRM_G.F][altName].minimapRad , GRM_AddonSettings_Save[GRM_G.F][altName].customPos , GRM_AddonSettings_Save[GRM_G.F][altName].minimapCustomPos , GRM_AddonSettings_Save[GRM_G.F][altName].CoreWindowPos , GRM_AddonSettings_Save[GRM_G.F][altName].reportChannel , GRM_AddonSettings_Save[GRM_G.F][altName].macroToolCoordinates } );

                local tempTable = GRM.DeepCopyArray ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] );  -- You need to set these values or else they won't carry over
                GRM_AddonSettings_Save[GRM_G.F][altName] = tempTable;      -- overwrite each player's settings with the current
                GRM_AddonSettings_Save[GRM_G.F][altName].minimapEnabled = tempSettingsHolder[1];
                GRM_AddonSettings_Save[GRM_G.F][altName].minimapPos = tempSettingsHolder[2];
                GRM_AddonSettings_Save[GRM_G.F][altName].minimapRad = tempSettingsHolder[3];
                GRM_AddonSettings_Save[GRM_G.F][altName].customPos = tempSettingsHolder[4];
                GRM_AddonSettings_Save[GRM_G.F][altName].minimapCustomPos = tempSettingsHolder[5];
                GRM_AddonSettings_Save[GRM_G.F][altName].CoreWindowPos = tempSettingsHolder[6];
                GRM_AddonSettings_Save[GRM_G.F][altName].reportChannel = tempSettingsHolder[7];
                GRM_AddonSettings_Save[GRM_G.F][altName].macroToolCoordinates = tempSettingsHolder[8];
            end
        end
    elseif not GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] then
        GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] = {};
    end
     
end

-- Method:          GRM.SyncAddonSettingsOfNewToon()
-- What it Does:    If a new player is joins the guild, and you already have a player in there, and one of the alts has it set to sync the settings in the guild,
--                  it loads the alt's settings.
-- Purpose:         Settings Sync feature...
GRM.SyncAddonSettingsOfNewToon = function()

    if GRM_G.guildName ~= "" and GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] ~= nil then
        for altName in pairs ( GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] ) do
            if altName ~= GRM_G.addonUser then

                -- Yes, sync needs to happen.
                if GRM_AddonSettings_Save[GRM_G.F][altName].syncSettings then
                    -- Ok, add all settings to a new array.
                    local tempTable = GRM.DeepCopyArray ( GRM_AddonSettings_Save[GRM_G.F][altName] );   
                    GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser] = tempTable;

                end

                break;
            end
        end
    elseif not GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] then
        GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] = {};
    end
end

-- Method:          GRM.SyncSettings()
-- What it Does:    Checks if player has it enabled to sync all addon settings, and if so, initiates that sync
-- Purpose:         To be triggered every single time a settings is modified.
GRM.SyncSettings = function()
    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncSettings then
        GRM.SyncAddonSettings();
    end
end


