-- Method:          GRM.GetGuildEventString ( int , string , string )
-- What it Does:    Gets more exact info from the actual Guild Event Log ( can only be queried once per 10 seconds) as a string
-- Purpose:         This parses more exact info, like "who" did the kicking, or "who" invited who, and so on.
GRM.GetGuildEventString = function ( index , playerName , initRank , finRank , class )
    -- index 1 = demote , 2 = promote , 3 = remove/quit , 4 = invite/join
    local added = false;
    local eventType = { "demote" , "promote" , "invite" , "join" , "quit" , "remove" };
    local logEntryMetaData = { false };

    if GRM_G.BuildVersion >= 30000 then -- Cata 2.3
        QueryGuildEventLog();

        if index == 1 or index == 2 then
            for i = GetNumGuildEvents() , 1 , -1 do
                local type , p1, p2 , _ , year , month , day , hour = GetGuildEventInfo ( i );
                if p1 ~= nil then                                                 ---or eventType [ 2 ] == type ) and ( p2 ~= nil and p2 == playerName ) and p1 ~= nil then
                    if index == 1 and eventType [ 1 ] == type and p2 ~= nil and ( p2 == playerName or p2 == GRM.SlimName ( playerName ) ) then          -- DEMOTIONS
                        p1 = GRM.GetStringClassColorByName ( p1 ) .. GRM.SlimName ( p1 ) .. "|r";
                        p2 = GRM.GetStringClassColorByName ( p2 ) .. GRM.SlimName ( p2 ) .. "|r";
                        logEntryMetaData = { true , p1 , p2 , initRank , finRank , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                        added = true;
                        break;
                    elseif index == 2 and eventType [ 2 ] == type and p2 ~= nil and ( p2 == playerName or p2 == GRM.SlimName ( playerName ) ) then      -- PROMOTIONS
                        p1 = GRM.GetStringClassColorByName ( p1 ) .. GRM.SlimName ( p1 ) .. "|r";
                        p2 = GRM.GetStringClassColorByName ( p2 ) .. GRM.SlimName ( p2 ) .. "|r";
                        logEntryMetaData = { true , p1 , p2 , initRank , finRank , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                        added = true;
                        break;
                    end
                end
            end

            -- Remove or Quit
       elseif index == 3 then
            local notFound = true;
            for i = GetNumGuildEvents() , 1 , -1 do 
                local type , p1, p2 , _ , year , month , day , hour = GetGuildEventInfo ( i );
                if p1 ~= nil then 
                    if eventType [ 5 ] == type or eventType [ 6 ] == type then   -- Quit or Kicked

                        -- KICKED
                        if eventType [ 6 ] == type and p2 ~= nil and ( p2 == playerName or p2 == GRM.SlimName ( playerName ) ) then
                            p1 = GRM.GetStringClassColorByName ( p1 ) .. GRM.SlimName ( p1 ) .. "|r";
                            p2 = GRM.GetStringClassColorByName ( p2 ) .. GRM.SlimName ( p2 ).. "|r";
                            logEntryMetaData = { true , p1 , p2 , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                            added = true;
                            notFound = false;

                        -- QUIT
                        elseif eventType [ 5 ] == type and ( p1 == playerName or p1 == GRM.SlimName ( playerName ) ) then
                            -- FOUND!
                            p1 = GRM.GetStringClassColorByName ( playerName ) .. GRM.SlimName ( playerName ) .. "|r";
                            logEntryMetaData = { true , p1 , nil , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                            added = true;
                            notFound = false;
                        end
                        if notFound ~= true then
                            break;
                        end
                    end
                end
            end
            -- Invite
        elseif index == 4 then
            for i = GetNumGuildEvents() , 1 , -1 do 
                local type , p1, p2 , _ , year , month , day , hour = GetGuildEventInfo ( i );
                if eventType [ 3 ] == type and p1 ~= nil and p2 ~= nil and ( p2 == playerName or p2 == GRM.SlimName ( playerName ) ) then  -- invite
                    p1 = GRM.GetStringClassColorByName ( p1 ) .. GRM.SlimName ( p1 ) .. "|r";
                    p2 = GRM.GetClassHex ( class ) .. GRM.SlimName ( p2 ) .. "|r";
                    logEntryMetaData = { true , p1 , p2 , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                    added = true;
                    break;
                end
            end

            -- JOINED but no invite data
            -- Possibly player joined but no record of *WHO* invited them
            if not added then
                for i = GetNumGuildEvents() , 1 , -1 do 
                    local type , p1, _ , _ , year , month , day , hour = GetGuildEventInfo ( i );  -- p1 = the player who joined
                    if eventType [ 4 ] == type and p1 ~= nil and ( p1 == playerName or p1 == GRM.SlimName ( playerName ) ) then  -- invite
                        p1 = GRM.GetClassHex ( class ) .. GRM.SlimName ( p1 ) .. "|r";
                        logEntryMetaData = { true , nil , p1 , GRM.GetTimestampBasedOnTimePassed ( { day , month , year , hour } ) };
                        added = true;
                        break;
                    end
                end
            end
        end
    end

    return added , logEntryMetaData;
end

-- Method:          GRM.RemoveNameFromAllREMAlts ( string )
-- What it Does:    Removes the player name from all potential stored points in the guild
-- Purpose:         No need to keep these points if they leave the guild as alt data is taken away.
GRM.RemoveNameFromAllREMAlts = function ( name )
    local guildData = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ];

    for _ , player in pairs ( guildData ) do
        if type ( player ) == "table" then
            if #player.removedAlts > 0 then
                for j = #player.removedAlts , 1 , -1 do
                    if name == player.removedAlts[j][1] then
                        table.remove ( player.removedAlts , j );
                    end
                end
            end
        end
    end

end

-- Method:          GRM.RecordKickChanges ( string , boolean , array , string )
-- What it Does:    Records and logs the changes for when a guildie either is KICKED or leaves the guild
-- Purpose:         Having its own function saves on repeating a lot of code here.
GRM.RecordKickChanges = function ( unitName , playerWasKicked , dateArray , officerThatKicked )
    local timestamp = GRM.GetTimestamp();
    local timeEpoch = time();

    local player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][unitName];
    local classColorCode = GRM.GetStringClassColorByName ( unitName );
    local timePassed = GRM.GetTimePlayerHasBeenMember ( unitName );
    local added = false;
    local logEntryMetaData = {};
    local listOfAlts = {};
    local tempStorage = { ( classColorCode .. unitName .. "|r" ) , playerWasKicked , timePassed };
    local isNoLongerOnServer = false;
    local playerLevel = 0;
    local customNote = "";

    -- Live detection
    if officerThatKicked ~= nil then
        added = true;
        local timeS , timeArray = GRM.GetTimestamp();
        logEntryMetaData = { true , GRM.GetStringClassColorByName ( officerThatKicked ) .. GRM.SlimName ( officerThatKicked ) .. "|r" , ( classColorCode .. unitName .. "|r" ) , { timeS , time() , timeArray } };
        timestamp = logEntryMetaData[4][1];
        timeEpoch = logEntryMetaData[4][2];
    -- Guild Event Log Detection
    else
        added , logEntryMetaData = GRM.GetGuildEventString ( 3 , unitName ); -- Kicked from the guild.
        if logEntryMetaData[1] then
            timestamp = logEntryMetaData[4][1];
            timeEpoch = logEntryMetaData[4][2];
            dateArray = logEntryMetaData[4][3];
        end
    end

    -- metaData = 4
    table.insert ( tempStorage , logEntryMetaData );
    
    -- Finding Player's record for removal of current guild and adding to the Left Guild table.
    if player ~= nil then
        -- Found!
        if player.customNote[6] ~= "" then
            customNote = player.customNote[6];
        end
        table.insert ( player.leftGuildDate , timestamp );                                       -- leftGuildDate
        table.insert ( player.leftGuildEpoch, timeEpoch );                                       -- leftGuildDateMeta

        if player.rankHistory[1][3] == 0 then
            table.insert ( player.rankHistory , { player.rankName , GRM.Trim ( string.sub ( timestamp , 1 , 10 ) ) , timeEpoch } )
        end

        table.insert ( player.rankHistory , { "|cFFC41F3BLeft Guild" , GRM.Trim ( string.sub ( timestamp , 1 , 10 ) ) , timeEpoch } );      -- Translate on show only, not here.
        player.oldRank = player.rankName;         -- old Rank on leaving.
        if #player.joinDate == 0 then                                                 -- Let it default to date addon was installed if date joined was never given
            table.insert( player.joinDate , player.rankHistory[1][2] );   -- oldJoinDate
            table.insert( player.joinDateEpoch , player.rankHistory[1][3] );   -- oldJoinDateMeta
        end

        -- If not banned, then let's ensure we reset his data.
        if not player.bannedInfo[1] then
            player.bannedInfo[1] = false;
            player.bannedInfo[2] = 0;
            player.bannedInfo[3] = false;
            player.bannedInfo[4] = "";
            player.reasonBanned = "";
        end
        
        playerLevel = player.level;
        -- Adding to LeftGuild Player history library
        GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][unitName] = {};
        GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][unitName] = GRM.DeepCopyArray ( player );
        
        -- Removing it from the alt list
        if #player.alts > 0 then

            -- Let's add them to the end of the report
            local countAlts = #player.alts;
            local isFound = false;

            for m = 1 , countAlts do
                isFound = false;

                -- Only do this if on a scan, not a live detection.
                if officerThatKicked == nil then
                    -- Verify the alt is not on the kick list already;
                    for r = 1 , #GRM_G.TempLeftGuildPlaceholder do
                        if GRM_G.TempLeftGuildPlaceholder[r][1] == player.alts[m][1] then
                            isFound = true;
                            break;
                        end
                    end
                end

                if not isFound then
                    table.insert ( listOfAlts , GRM.GetClassifiedName ( player.alts[m][1] , true ) );
                end
            end
            
            -- Let's overwrite the listOfAts
            local tempListOfAlts = GRM.DeepCopyArray ( player.alts );
            GRM.RemoveAlt ( player.alts[1][1] , unitName , false , 0 , false );                 -- removes from the current guild alt list...

            -- Set alt to main if it is only one left.

            if #tempListOfAlts == 1 then
                local tempEpochTime = 0;
                if added then
                    tempEpochTime = timeEpoch;
                end
                GRM.SetMain ( tempListOfAlts[1][1] , tempListOfAlts[1][1] , true , tempEpochTime );
            end
            
            -- Remove them from the playersLists as well...
            GRM.RemoveNameFromAllREMAlts ( unitName );

            -- Re-add the alt lists to the player now that they have left the guild...
            player = GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][unitName];
            if player ~= nil then
                player.alts = tempListOfAlts;         -- Stores the alt list in the left player databse. It will wipe on rejoin, but good to know...
            end
        end
        -- removing from active member library
        GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][unitName] = nil;
    end
    table.insert ( tempStorage , listOfAlts );

    -- Update the live frames too!
    if GRM_UI.GRM_RosterChangeLogFrame.GRM_CoreBanListFrame:IsVisible() then
        GRM.RefreshBanListFrames();
    end
    
    local mainName , playerHasAlts = GRM.GetMainName ( unitName , false );
    if mainName ~= "" and mainName ~= unitName and playerHasAlts then
        -- Ok, if they have no mainName, let's check the left player's list for matches..
        table.insert ( tempStorage , GRM.GetClassifiedName ( mainName , true ) );

    else
        table.insert ( tempStorage , "" );
    end

    -- Now, let's add their officer/public notes.
    local p , o = "" , "";
    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addNotesToLeft then
        player = GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][ GRM_G.guildName ][unitName];
        if player ~= nil then
            if ( player.note ~= nil and player.note ~= "" ) then
                p = player.note;                
            end
            if ( player.officerNote ~= nil and player.officerNote ~= "" and GRM.CanViewOfficerNote() ) then
                o = player.officerNote;
            end
        end
    end
    -- Let's get it added to the log
    return tempStorage[1] , tempStorage[2] , tempStorage[3] , tempStorage[4] , tempStorage[5] , tempStorage[6] , p , o , dateArray , added , isNoLongerOnServer , playerLevel , customNote;
end

-- Method:          GRM.RecordLeftGuildChanges ( name , array )
-- What it Does:    Records and saves the information when a player leaves the guild, and updates the frames
-- Purpose:         Useful for logging changes that they left the guild on their own, not just kicked.
GRM.RecordLeftGuildChanges = function ( unitName , dateArray , isLiveDetection )
    local timestamp = GRM.GetTimestamp();
    local timeEpoch = time();

    local player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][unitName];
    local timePassed = GRM.GetTimePlayerHasBeenMember ( unitName );
    local listOfAlts = {};
    local logEntryMetaData = { true , GRM.GetClassifiedName ( unitName , true ) , nil };
    local isNoLongerOnServer = false;
    local isFoundInEventLog = false;
    local playerLevel = 0;
    local customNote = "";
    local p , o = "" , "";  -- public/officer Notes
    local mainName = "";
    
    -- Finding Player's record for removal of current guild and adding to the Left Guild table.
    if player ~= nil then
        -- Found!

        -- Now, let's add their officer/public notes.
        if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addNotesToLeft then
            if ( player.note ~= nil and player.note ~= "" ) then
                p = player.note;                
            end
            if ( player.officerNote ~= nil and player.officerNote ~= "" and GRM.CanViewOfficerNote() ) then
                o = player.officerNote;
            end
            if player.customNote[6] ~= "" then
                customNote = player.customNote[6];
            end
        end

        mainName = GRM.GetMainName ( unitName , false );

        table.insert ( player.leftGuildDate, timestamp );                                       -- leftGuildDate
        table.insert ( player.leftGuildEpoch, timeEpoch );                                       -- leftGuildDateMeta
        
        if player.rankHistory[1][3] == 0 then
            table.insert ( player.rankHistory , { player.rankName , GRM.Trim ( string.sub ( timestamp , 1 , 10 ) ) , timeEpoch } )
        end

        table.insert ( player.rankHistory , { "|cFFC41F3BLeft Guild" , GRM.Trim ( string.sub ( timestamp , 1 , 10 ) ) , timeEpoch } );      -- Translate on show only, not here.
        player.oldRank = player.rankName;         -- old Rank on leaving.
        if #player.joinDate == 0 then                                                 -- Let it default to date addon was installed if date joined was never given
            table.insert( player.joinDate , player.rankHistory[1][2] );                         -- oldJoinDate
            table.insert( player.joinDateEpoch , player.rankHistory[1][3] );   -- oldJoinDateMeta
        end

        -- If not banned, then let's ensure we reset his data.
        if not player.bannedInfo[1] then
            player.bannedInfo[1] = false;
            player.bannedInfo[2] = 0;
            player.bannedInfo[3] = false;
            player.bannedInfo[4] = "";
            player.reasonBanned = "";
        end
        playerLevel = player.level;

        -- Adding to LeftGuild Player history library
        GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][unitName] = player;

        -- Removing it from the alt list
        if #player.alts > 0 then
            for m = 1 , #player.alts do
                table.insert ( listOfAlts , GRM.GetClassifiedName ( player.alts[m][1] , true ) );
            end

            -- Let's overwrite the listOfAts
            local tempListOfAlts = GRM.DeepCopyArray ( player.alts );
            GRM.RemoveAlt ( player.alts[1][1] , player.name , false , 0 , false );                 -- removes from the current guild alt list...
            
            -- Remove them from the playersLists as well...
            GRM.RemoveNameFromAllREMAlts ( player.name );

            -- Re-add the alt lists to the player now that they have left the guild...
            player = GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][unitName];
            if player ~= nil then
                player.alts = tempListOfAlts;         -- Stores the alt list in the left player databse. It will wipe on rejoin, but good to know...
            end
        end
        -- removing from active member library
        GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][unitName] = nil;
    end
    
    -- Let's get it added to the log
    return unitName , false , timePassed , logEntryMetaData , listOfAlts , mainName , p , o , dateArray , isFoundInEventLog , isNoLongerOnServer , isLiveDetection , playerLevel , customNote;
end


-- Method:          GRM.IsRejoinAndSetDetails ( table , string , string , bool , array )
-- What it Does:    Checks the left player meta data, to the new player joining, and determins if they are rejoining, and if so, if they were banned
--                  It also manages setting the new officer/public note join date, if player uses said feature.
-- Purpose:         Special feature, scanning for changes, live tracking updates as player joins as well... and compartmentalizing this long function.
GRM.IsRejoinAndSetDetails = function( member , simpleName , tempTimeStamp , scanUpdate , tempJoinStorage )
    local guildData = GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName];
    local useTimeStamp = false;
    local timeStamp = "";

    tempJoinStorage[5] = false;                 -- is a rejoin
    tempJoinStorage[6] = 0;                     -- Player Level
    tempJoinStorage[7] = "";                    -- The previousName
    tempJoinStorage[8] = 0;                     -- Number of Times in Guild
    tempJoinStorage[9] = false;                 -- IsBanned
    tempJoinStorage[10] = "";                   -- reasonBanned
    tempJoinStorage[11] = "";                   -- date left
    tempJoinStorage[12] = -1;                   -- How Long Ago
    tempJoinStorage[13] = "";                   -- Date originally joined
    tempJoinStorage[14] = "";                   -- Original Rank.
    tempJoinStorage[15] = "";                   -- Custom Notes

    local player;
    for formerMember in pairs ( guildData ) do -- Number of players that have left the guild.
        player = guildData[formerMember];

        if type ( player ) == "table" then
            if member.GUID == player.GUID then  -- GUID comparison check - cannot rely purely on the name.
                tempJoinStorage[5] = true;
                -- MATCH FOUND - Player is RETURNING to the guild!
                
                -- Player has name-changed?
                if member.name ~= player.name then
                    local classColorString = GRM.GetStringClassColorByName ( player.name , true );

                    -- For the logging
                    tempJoinStorage[7] = ( classColorString .. GRM.SlimName ( player.name ) .. "|r" );
                    -- Update the banned player's name
                    player.name = member.name;
                end

                
                -- number of times in the guild
                tempJoinStorage[8] = #player.joinDate;
                
                
                if tempTimeStamp ~= "1 Jan '01 12:01am" and tempTimeStamp ~= ( "1 Jan '01 00:01" .. GRM.L ( "24HR_Notation" ) ) then
                    timeStamp = tempTimeStamp;
                    useTimeStamp = true;
                else
                    timeStamp = GRM.GetTimestamp();
                end

                -- Universal Rejoin data whether banned or not
                -- Date player left the guild
                if #player.leftGuildDate > 0 then
                    tempJoinStorage[11] = player.leftGuildDate[#player.leftGuildDate];
                end
                -- How long ago that was
                if #player.leftGuildEpoch > 0 and tempJoinStorage[10] ~= "" then
                    tempJoinStorage[12] = player.leftGuildEpoch[#player.leftGuildEpoch];
                end
                -- Date originally joined the guild the first time ever
                if #player.joinDate > 0 then
                    tempJoinStorage[13] = player.joinDate[1];
                end
                -- Set original Rank
                tempJoinStorage[14] = player.oldRank;

                -- Set Custom Note details
                if player.customNote[6] ~= "" then
                    tempJoinStorage[15] = player.customNote[6];
                end

                -- isBanned
                local index = 0;
                if player.bannedInfo[1] then
                    index = 9;
                    tempJoinStorage[9] = true;
                    tempJoinStorage[10] = player.reasonBanned;
                                
                    if not scanUpdate then
                        -- Written fully out, not as necessary, but to make the next section easier to work with with specific named arguments rather than 16 indexes of an array
                        GRM.AddBanRejoinEntry ( 
                            tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] , tempJoinStorage[7] , tempJoinStorage[8] ,
                            tempJoinStorage[9] , tempJoinStorage[10] , tempJoinStorage[11] , tempJoinStorage[12] , tempJoinStorage[13] , tempJoinStorage[14] , tempJoinStorage[15]
                        );
                    else
                        useTimeStamp = true;
                    end
                else
                    index = 7;
                    -- No Ban found, player just returning!
                    -- Set member level
                    tempJoinStorage[6] = member.level;

                    if not scanUpdate then
                        -- Written fully out, not as necessary, but to make the next section easier to work with with specific named arguments rather than 16 indexes of an array
                        GRM.AddNormalRejoinEntry ( 
                            tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] , tempJoinStorage[7] , tempJoinStorage[8] ,
                            tempJoinStorage[9] , tempJoinStorage[10] , tempJoinStorage[11] , tempJoinStorage[12] , tempJoinStorage[13] , tempJoinStorage[14] , tempJoinStorage[15]
                        );
                    else
                        useTimeStamp = true;
                    end
                end
                
                -- Needs to be reported immediately rather than waiting.
                if scanUpdate then
                    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser]["toChat"].joined then
                        GRM.PrintLog ( 
                            {
                                index , 
                                select ( 2 , GRM.GetJoinOrRejoinString (
                                    tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] , tempJoinStorage[7] , tempJoinStorage[8] ,
                                    tempJoinStorage[9] , tempJoinStorage[10] , tempJoinStorage[11] , tempJoinStorage[12] , tempJoinStorage[13] , tempJoinStorage[14] , tempJoinStorage[15]
                                ) )
                            }
                        );
                    end
                    GRM.AddLog (
                        {
                            index , 
                            GRM.GetJoinOrRejoinString (
                                tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] , tempJoinStorage[7] , tempJoinStorage[8] ,
                                tempJoinStorage[9] , tempJoinStorage[10] , tempJoinStorage[11] , tempJoinStorage[12] , tempJoinStorage[13] , tempJoinStorage[14] , tempJoinStorage[15]
                            ), 
                            tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] , tempJoinStorage[7] , tempJoinStorage[8] ,
                            tempJoinStorage[9] , tempJoinStorage[10] , tempJoinStorage[11] , tempJoinStorage[12] , tempJoinStorage[13] , tempJoinStorage[14] , tempJoinStorage[15]
                        }
                    );
                end

                -- Adding timestamp to new Player.
                local noteIsSet = false;
                local officerNoteIsSet = false;
                if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addTimestampToNote and useTimeStamp then
                    local index;

                    if member.rosterSelection == 0 then
                        for i = 1 , GRM.GetNumGuildies() do
                            local name = GetGuildRosterInfo ( i );
                            if name == member.name then
                                index = i;
                                break;
                            end
                        end
                    else
                        index = member.rosterSelection;
                    end

                    if index ~= nil then
                        local name ,_,_,_,_,_, note , oNote = GetGuildRosterInfo( member.rosterSelection );
                        if name == member.name then
                            local t;
                            if not tempJoinStorage[1] then
                                t = GRM.GetTimestamp();
                                t = string.sub ( t , 1 , string.find ( t , "'" ) + 2 );
                            else
                                t = string.sub ( timeStamp , 1 , string.find ( timeStamp , "'" ) + 2 );
                            end
                            t = GRM.FormatTimeStamp ( t , false );
                            local noteToSet = "";
                            if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].includeTag then
                                noteToSet = GRM_G.customHeaderRejoin .. " " .. t;
                            else
                                noteToSet = t;
                            end

                            if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 1 and  GRM.CanEditOfficerNote() and ( oNote == "" or oNote == nil ) then
                                officerNoteIsSet = true;
                                GuildRosterSetOfficerNote( member.rosterSelection , noteToSet );
                            elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 2 and CanEditPublicNote() and ( note == "" or note == nil ) then
                                noteIsSet = true;
                                GuildRosterSetPublicNote ( member.rosterSelection , noteToSet );
                            elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 3 then
                                GRM.SetJoinDateToCustomNote ( member.name , noteToSet );
                            end
                        end
                    end
                end
                
                -- Make sure to include the officer and public notes if necessary as well for new players
                -- Public note
                if noteIsSet and not scanUpdate then
                    -- Add the public note!
                    if member.note ~= "" then
                        GRM.AddNoteTempLogEntry ( GRM.GetClassifiedName ( member.name , true ) , "" , member.note , select ( 2 , GRM.GetTimestamp() ) );
                    end
                end

                -- Officer Note
                if officerNoteIsSet and not scanUpdate then
                    -- Add the Officer note!
                    if member.officerNote ~= "" then
                        player.officerNote = member.officerNote;
                    end
                end

                -- Make sure the namechange is adjusted in the database or you will get a double report
                GRM.AddMemberRecord( member , true , GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][formerMember] );
                
                -- Removing Player from LeftGuild History (Yes, they will be re-added upon leaving the guild.)
                GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][GRM_G.guildName][formerMember] = nil;
                player = GRM_GuildMemberHistory_Save[ GRM_G.F ][GRM_G.guildName][member.name];

                -- It must be done AFTER the record has been added
                -- Promotion Info
                if not scanUpdate and member.rankIndex < ( GuildControlGetNumRanks() - 1 ) then
                    -- Promotion Obtained since joining!
                    local timestamp2 , dateArray = GRM.GetTimestamp();
                    local epochTime = time();
                    local nameOfBaseRank = GuildControlGetRankName( GuildControlGetNumRanks() );
                    local added , logEntryMetaData = GRM.GetGuildEventString ( 2 , member.name , nameOfBaseRank , member.rankName );
                    
                    -- I don't want to have an instance where I have the promotion date in the log but the join date is too old os it has fallen off,
                    -- Thus, if the exact join date cannot be determined, it will set the promotion date to be no different.
                    if useTimeStamp then

                        -- Ok data is saved! Now let's report it to the log...
                        if added then
                            timestamp2 = logEntryMetaData[6][1];
                            epochTime = logEntryMetaData[6][2];
                        else
                            logEntryMetaData = { logEntryMetaData[1] , nil , simpleName , nameOfBaseRank , member.rankName , dateArray };
                        end

                        -- Clear if set to unknown
                        player.promoteDateUnknown = false;

                        -- For SYNC
                        if not added then
                            -- Use old stamps so as not to override other player data...
                            timestamp2 = "1 Jan '01 12:01am";
                            epochTime = 978375660;
                        else
                            player.verifiedPromoteDate[1] = timestamp2;
                            player.verifiedPromoteDate[2] = epochTime;
                        end

                        
                    end

                    player.rankName = member.rankName; -- Saving new rank Info
                    player.rankIndex = member.rankIndex; -- Saving new rank Index Info

                    if player.rankHistory[1][3] == 0 then
                        player.rankHistory[1][1] = player.rankName;
                        player.rankHistory[1][2] = string.sub ( timestamp2 , 1 , string.find ( timestamp2 , "'" ) + 2 )
                        player.rankHistory[1][3] = epochTime;
                    else
                        table.insert ( player.rankHistory , { player.rankName , string.sub ( timestamp2 , 1 , string.find ( timestamp2 , "'" ) + 2 ) , epochTime } ); -- New rank, date, metatimestamp
                    end

                    -- Ok data is saved! Now let's report it to the log...
                    if added then
                        GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , logEntryMetaData[2] , logEntryMetaData[3] , logEntryMetaData[4] , logEntryMetaData[5] , logEntryMetaData[6][3] );
                    else
                        GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , nil , simpleName , nameOfBaseRank , member.rankName , select ( 2 , GRM.GetTimestamp() ) );
                    end
                end            
                break;
            end
        end
    end

    -- Set the level if not joining.
    if not tempJoinStorage[5] then
        tempJoinStorage[6] = member.level;
    end
    
    return tempJoinStorage[5] , member , simpleName , tempTimeStamp , tempJoinStorage
end

-- Method:          GRM.RecordJoinChanges ( array , string , boolean , array , boolean )
-- What it Does:    Checks and records the new player changes... are they a returning player or completely new. Were they previously banned?
-- Purpose:         Keep the methods clean by compartmentalizing this rather lengthy function. It is also useful to not double the code as this will be called to on a live tracked event.
GRM.RecordJoinChanges = function ( member , simpleName , scanUpdate , dateArray , isVerified )
    -- Check against old member list first to see if returning player!
    local rejoin = false;
    -- Use default dates, since these are auto-tagged, you don't want your data to overwrite any others, so set it as OLD...
    local tempTimeStamp = "1 Jan '01 12:01am";
    local timeEpoch = 978375660;
    local added , logEntryMetaData = GRM.GetGuildEventString ( 4 , member.name , nil , nil , member.class ); -- For determining who did the invite.
    local currentTime = GRM.GetTimestamp();
    local tempJoinStorage = {};
    local player;

    if added then
        tempTimeStamp = logEntryMetaData[4][1];
        timeEpoch = logEntryMetaData[4][2];
        tempJoinStorage = { logEntryMetaData[1] , logEntryMetaData[2] , logEntryMetaData[3] , logEntryMetaData[4][3] };
    else
        tempJoinStorage = { false , nil , simpleName , dateArray };
    end
    
    if not added and scanUpdate then
        tempTimeStamp = currentTime;
        timeEpoch = time();
        added = true;
    end
    -- { isAdded , inviter (nil) , invitedPlayer , { day , month , year , hour } }
    rejoin , member , simpleName , tempTimeStamp , tempJoinStorage  = GRM.IsRejoinAndSetDetails ( member , simpleName , tempTimeStamp , scanUpdate , tempJoinStorage )
            
    if not rejoin then
        -- New Guildie. NOT a rejoin!
        local t;
        local timeStamp;
        if tempTimeStamp ~= "1 Jan '01 12:01am" and tempTimeStamp ~= ( "1 Jan '01 00:01" .. GRM.L ( "24HR_Notation" ) ) then
            timeStamp = tempTimeStamp;
        else
            timeStamp = currentTime;
        end

        if not added then
            t = currentTime;
            t = string.sub ( t , 1 , string.find ( t , "'" ) + 2 );
        else
            t = string.sub ( timeStamp , 1 , string.find ( timeStamp , "'" ) + 2 );
        end

        t = GRM.FormatTimeStamp ( t , false );
        local finalTStamp = "";
        if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].includeTag then
            finalTStamp = GRM_G.customHeaderJoin .. " " .. t;
        else
            finalTStamp = t;
        end
        
        -- Adding timestamp to new Player.
        local currentOfficerNote = tostring ( member.officerNote );
        local currentPublicNote = tostring ( member.note );
        local noteIsSet = false;
        local officerNoteIsSet = false;

        if added and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addTimestampToNote and ( GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 1 or GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 2 ) then
            local name ,_,_,_,_,_, note , oNote = GetGuildRosterInfo ( member.rosterSelection );
            if name == member.name then
                if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 1 and GRM.CanEditOfficerNote() and ( oNote == "" or oNote == nil ) then
                    GuildRosterSetOfficerNote( member.rosterSelection , finalTStamp );
                    officerNoteIsSet = true
                    if scanUpdate then
                        member.officerNote = finalTStamp;
                    end
                elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 2 and CanEditPublicNote() and ( note == "" or note == nil ) then
                    GuildRosterSetPublicNote ( member.rosterSelection , finalTStamp );
                    noteIsSet = true;
                    if scanUpdate then
                        member.note = finalTStamp;
                    end
                end
                -- Of note, cannot do adding custom note yet here because that relies on them being in the database. Profile needs to be built first which happens in a moment
            end
        end

        -- Adding to global saved array, adding to report 
        GRM.AddMemberRecord ( member , false , nil );

        -- now we can add the custom note
        if added and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addTimestampToNote and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 3 then
            GRM.SetJoinDateToCustomNote ( member.name , finalTStamp );
        end

        
        if not scanUpdate then
            GRM.AddJoinEntry ( tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6] );
        else

            if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser]["toChat"].joined then
                GRM.PrintLog ( 
                    {
                        8 , 
                        select ( 2 , GRM.GetJoinOrRejoinString (
                            tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6]
                        ) )
                    }
                );
            end
            GRM.AddLog (
                {
                    8 , 
                    GRM.GetJoinOrRejoinString (
                        tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6]
                    ) ,
                    tempJoinStorage[1] , tempJoinStorage[2] , tempJoinStorage[3] , tempJoinStorage[4] , tempJoinStorage[5] , tempJoinStorage[6]
                } 
            ) 
        end

        player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][member.name];
        -- adding join date to history and rank date.
        if player ~= nil then

            -- Add the tempTimeStamp to officer note... this avoids report spam
            -- Promo Date stamp
            if tempTimeStamp ~= "1 Jan '01 12:01am" and tempTimeStamp ~= ( "1 Jan '01 00:01" .. GRM.L ( "24HR_Notation" ) ) then
                -- Clear if set to unknown
                player.promoteDateUnknown = false;
                
                -- Join Date stamp
                -- No need to check size of table, it will be the first index as the player data was just added.
                table.insert ( player.joinDate , timeStamp );
                table.insert ( player.joinDateEpoch , timeEpoch );

                -- For Event tracking!
                local date = GRM.ConvertGenericTimestampToIntValues ( string.sub ( timeStamp , 1 , string.find ( timeStamp , "'" ) + 2 ) );
                player.events[1][1][1] = date[1];
                player.events[1][1][2] = date[2];
                player.events[1][1][3] = date[3];
            else
                -- UNVERIFIED DATA!!!!
                local tStamp = GRM.GetTimestamp();

                -- Clear if set to unknown
                player.promoteDateUnknown = false;
                
                -- Join Date stamp
                -- No need to check size of table, it will be the first index as the player data was just added.
                table.insert ( player.joinDate , tStamp );
                table.insert ( player.joinDateEpoch , time() );

                -- For Event tracking!
                local date = GRM.ConvertGenericTimestampToIntValues ( string.sub ( tStamp , 1 , string.find ( tStamp , "'" ) + 2 ) );
                player.events[1][1][1] = date[1];
                player.events[1][1][2] = date[2];
                player.events[1][1][3] = date[3];
            end
            
            if added then 
                -- For SYNC
                -- Join Date
                player.verifiedJoinDate[1] = tempTimeStamp;
                player.verifiedJoinDate[2] = timeEpoch;
                -- Promo Date
                player.verifiedPromoteDate[1] = tempTimeStamp;
                player.verifiedPromoteDate[2] = timeEpoch;

                if player.rankHistory[1][3] == 0 then
                    player.rankHistory[1][1] = player.rankName;
                    player.rankHistory[1][2] = string.sub ( tempTimeStamp , 1 , string.find ( tempTimeStamp , "'" ) + 2 )
                    player.rankHistory[1][3] = timeEpoch;
                else
                    table.insert ( player.rankHistory , { player.rankName , string.sub ( tempTimeStamp , 1 , string.find ( tempTimeStamp , "'" ) + 2 ) , timeEpoch } ); -- New rank, date, metatimestamp
                end
            end

            if not scanUpdate and added then
                if currentOfficerNote == nil or currentOfficerNote == "" then
                    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addTimestampToNote then
                        if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 1 and GRM.CanEditOfficerNote() and ( player.officerNote == "" or player.officerNote == nil ) then
                            player.officerNote = finalTStamp;
                        elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 2 and CanEditPublicNote() and ( player.note == "" or player.note == nil ) then
                            player.note = finalTStamp;
                        elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 3 then
                            GRM.SetJoinDateToCustomNote ( member.name , finalTStamp );
                        end
                    end

                elseif currentOfficerNote ~= nil and currentOfficerNote ~= "" then
                    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].addTimestampToNote then
                        if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 1 and GRM.CanEditOfficerNote() and ( player.officerNote == "" or player.officerNote == nil ) then
                            player.officerNote = currentOfficerNote;
                        elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 2 and CanEditPublicNote() and ( player.note == "" or player.note == nil ) then
                            player.note = currentPublicNote;
                        elseif GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].joinDateDestination == 3 then
                            GRM.SetJoinDateToCustomNote ( member.name , finalTStamp );
                        end
                    end
                end
            end

            -- Let's check the notes
            if added then
                if noteIsSet and not scanUpdate and member.note ~= "" then
                    -- Add the public note!
                    GRM.AddNoteTempLogEntry ( GRM.GetClassifiedName ( member.name , true ) , "" , member.note , select ( 2 , GRM.GetTimestamp() ) );

                end
                -- Officer Note
                if officerNoteIsSet and not scanUpdate and member.officerNote ~= "" then
                    -- Add the Officer note!
                    GRM.AddOfficerNoteTempLogEntry ( GRM.GetClassifiedName ( member.name , true ) , "" , member.officerNote , select ( 2 , GRM.GetTimestamp() ) );
                end
            end

            -- Promotion Info
            if member.rankIndex < ( GuildControlGetNumRanks() - 1 ) then
                -- Promotion Obtained since joining!
                local timestamp2 = currentTime;
                local epochTime = time();
                local nameOfBaseRank = GuildControlGetRankName( GuildControlGetNumRanks() );
                local added , logEntryMetaData = GRM.GetGuildEventString ( 2 , member.name , nameOfBaseRank , member.rankName );
                local rankHistoryIsGood = false;

                -- I don't want to have an instance where I have the promotion date in the log but the join date is too old os it has fallen off,
                -- Thus, if the exact join date cannot be determined, it will set the promotion date to be no different.
                if tempTimeStamp ~= "1 Jan '01 12:01am" and tempTimeStamp ~= ( "1 Jan '01 00:01" .. GRM.L ( "24HR_Notation" ) ) then
                    if added then
                        timestamp2 = logEntryMetaData[6][1];
                        epochTime = logEntryMetaData[6][2];
                        rankHistoryIsGood = true;
                    end

                    -- Clear if set to unknown
                    player.promoteDateUnknown = false;

                    -- For SYNC
                    if not added then
                        -- Use old stamps so as not to override other player data...
                        local time = "";
                        if not GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].twentyFourHrScale then
                            time = "12:01am";
                        end
                        timestamp2 = "1 Jan '01 " .. time;
                        epochTime = 978375660;
                    else
                        player.verifiedPromoteDate[1] = timestamp2;
                        player.verifiedPromoteDate[2] = epochTime;

                        if player.rankHistory[1][3] == 0 and rankHistoryIsGood then
                            player.rankHistory[1][1] = member.rankName;
                            player.rankHistory[1][2] = string.sub ( timestamp2 , 1 , string.find ( timestamp2 , "'" ) + 2 )
                            player.rankHistory[1][3] = epochTime;
                        else

                            -- this applies if the previous date was unable to be verified so it set it to the current date, but the 2nd promo was, setting it to earlier date.
                            -- applying the new date appropriately
                            if player.rankHistory[#player.rankHistory][3] > epochTime then 
                                player.rankHistory[#player.rankHistory][3] = epochTime;
                                player.rankHistory[#player.rankHistory][2] = string.sub ( timestamp2 , 1 , string.find ( timestamp2 , "'" ) + 2 );
                            end

                            table.insert ( player.rankHistory , { player.rankName , string.sub ( timestamp2 , 1 , string.find ( timestamp2 , "'" ) + 2 ) , epochTime } ); -- New rank, date, metatimestamp
                        end

                    end

                end
                
                player.rankName = member.rankName; -- Saving new rank Info
                player.rankIndex = member.rankIndex; -- Saving new rank Index Info
                
                -- Ok data is saved! Now let's report it to the log...
                
                if logEntryMetaData[1] then
                    GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , logEntryMetaData[2] , logEntryMetaData[3] , logEntryMetaData[4] , logEntryMetaData[5] , logEntryMetaData[6][3] );
                else
                    GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , nil , GRM.GetClassifiedName ( member.name , true ) , nameOfBaseRank , member.rankName , dateArray );
                end
            end
        end
    end

    -- This happened live and is 100% verified
    if isVerified and GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][member.name] ~= nil then
        player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][member.name];

        player.verifiedJoinDate[1] = player.joinDate[#player.joinDate];
        player.verifiedJoinDate[2] = player.joinDateEpoch[#player.joinDateEpoch];
        player.verifiedPromoteDate[1] = tostring ( player.verifiedJoinDate[1] );   -- to disassociate 
        player.verifiedPromoteDate[2] = tonumber ( player.verifiedJoinDate[2] );   -- to disassociate 

    end

    -- Live detection it refreshes the logs, not on first scan check on logon.
    if scanUpdate then
        GRM.RefreshSelectHybridFrames ( true , true , true , true );
    end
end

-- Method:          GRM.RecordCustomNoteChanges ( string , string , string , boolean )
-- What it Does:    Reports the Custom note changes to the Guild Log
-- Purpose:         For ease of reporting to the log, as a UI feature.
GRM.RecordCustomNoteChanges = function( newNote , oldNote , editorName , editedName , rebuildLog )
    -- Remove the linebreaks in the log reporting or it will be spammy. Replace with a dash
    local nNote = string.gsub ( newNote , "\n" , "-" );
    local oNote = string.gsub ( oldNote , "\n" , "-" );

    local logReportWithTime , logReport = GRM.GetCustomNoteChangeString ( nNote , oNote , GRM.GetClassifiedName ( editorName , true ) , GRM.GetClassifiedName ( editedName , true ) , select ( 2 , GRM.GetTimestamp() ) );
    -- Ok that to the log...
    if rebuildLog and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser]["toChat"].customNote then
        GRM.PrintLog ( { 19 , logReport } );
    end

    GRM.AddLog ( { 19 , logReportWithTime , nNote , oNote , GRM.GetClassifiedName ( editorName , true ) , GRM.GetClassifiedName ( editedName , true ) , select ( 2 , GRM.GetTimestamp() ) } );
    if rebuildLog and GRM_UI.GRM_RosterChangeLogFrame:IsVisible() and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser]["toLog"].customNote then
        GRM_G.LogNumbersColorUpdate = true;
        GRM.BuildLogComplete( true , true );
    end

    if GRM_UI.GRM_MemberDetailMetaData:IsVisible() and editedName == GRM_G.currentName and GRM_UI.GRM_MemberDetailMetaData.GRM_ConfirmCustomNoteButton:IsVisible() then
        GRM_UI.GRM_MemberDetailMetaData.GRM_ConfirmCustomNoteButton:Hide();
        GRM_UI.GRM_MemberDetailMetaData.GRM_CancelCustomNoteButton:Hide();
        GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:SetText ( GRM_G.OriginalEditBoxValue );
        GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:ClearFocus();
        GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteTextCount:Hide();
        
        if GRM_G.currentName ~= GRM_G.addonUser then
            GRM_UI.GRM_MemberDetailMetaData.GRM_SafeFromRulesButton:Show();
            local player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][ GRM_G.currentName ];
            if player ~= nil then
                if player.isOnline then
                    GRM.SetGroupInviteButton ( GRM_G.currentName );
                    GRM_UI.GRM_MemberDetailMetaData.GRM_GroupInviteButton:Show();
                end
            end
        end
        
        GRM.BuildCustomNoteScrollFrame ( GRM_G.OriginalEditBoxValue );
        if GRM_UI.GRM_MemberDetailMetaData.GRM_DateSubmitButton:IsVisible() ~= true then            -- Does not unpause if the date still needs to be selected or canceled.
            GRM_UI.Unpause();
        end
    end
end

-- Method:          GRM.SetCustomNote();
-- What it Does:    Modifies the custom note to new one, if necessary and sends proper updates and comms
-- Purpose:         Reduce a bit of code bloat. Make the UI functions of the editbox more readable.
GRM.SetCustomNote = function()
    local player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][GRM_G.currentName];
    if player ~= nil then
        -- The trim is so that just a white space doesn't somehow count as a new note.
        if GRM.Trim ( GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:GetText() ) ~= player.customNote[6] then
            local oldNote = player.customNote[6];
            player.customNote[2] = time();
            player.customNote[3] = GRM_G.addonUser;
            player.customNote[6] = GRM.Trim ( GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:GetText() );
            GRM_G.OriginalEditBoxValue = player.customNote[6];  -- This needs to be set to handle the OnEditFocusLost logic..
            -- Handle Log reporting logic here...

            GRM.RecordCustomNoteChanges ( player.customNote[6] , oldNote , GRM_G.addonUser , player.name , true )

            -- Handle live sync SendMessage here...
            if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncEnabled and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncRankCustom and player.customNote[1] then
                local tempMsg = GRM_G.PatchDayString .. "?GRM_CNOTE?" .. GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncRankCustom .. "?" .. player.customNote[4] .. "?#"  .. player.name .. "?#" .. tostring ( player.customNote[2] );
                local customNote = "";
                if #player.customNote[6] > 0 then
                    customNote = player.customNote[6];
                else
                    customNote = "X&&X";
                end
                -- Check size... if need to split
                if ( #tempMsg + #customNote + GRMsyncGlobals.sizeModifier ) > 254 then
                    -- size is too big! let's split it!
                    GRMsync.SendMessage ( "GRM_SYNC" , GRM_G.PatchDayString .. "?GRM_CNOTE?" .. GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncRankCustom .. "?" .. string.sub ( customNote , #customNote - 49 ) .. "&X&" , "GUILD" );
                    -- now we send all the data in followup...
                    GRMsync.SendMessage ( "GRM_SYNC" , tempMsg .. "?#" .. string.sub ( customNote , 1 , #customNote - 50 ) .. "&X&" , "GUILD" );
                else
                    GRMsync.SendMessage ( "GRM_SYNC" , tempMsg  .. "?#" .. customNote , "GUILD" );
                end
            end
        end

        if GRM.Trim ( GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:GetText() ) == "" then
            GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:SetText ( GRM.L ( "Click here to set Custom Notes" ) );
            GRM_G.OriginalEditBoxValue = GRM.L ( "Click here to set Custom Notes" );
        else
            GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:SetText ( player.customNote[6] );
        end

    end
    GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:ClearFocus();
end

-- Method:          GRM.SetJoinDateToCustomNote ( string , string )
-- What it Does:    Checks to see if new note can merge with old custom note and be less than or equal to 150 characters. If not, it does nothing
-- Purpose:         To allow the player to auto update the join date to the custom note window.
GRM.SetJoinDateToCustomNote = function( playerName , noteToSet )
    local player = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ][playerName];

    if player == nil then
        player = GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][ GRM_G.guildName ][playerName];
        if player == nil then
            return;
        end 
    end

    local newNote = noteToSet;
    local oldNote = player.customNote[6];
    local goodToSet = false;
    -- Check note size...
    if oldNote == "" then
        goodToSet = true;
    else
        if string.find ( oldNote , newNote , 1 , true ) == nil then  -- No need to merge if note already there
            newNote = newNote .. "\n" .. oldNote;   -- Ok, merged the notes...
            if #newNote <= GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:GetMaxLetters() then
                goodToSet = true;
            end
        end
    end
    -- Update it, report it!
    if goodToSet then
        player.customNote[2] = time();
        player.customNote[3] = playerName;
        player.customNote[6] = newNote;
        GRM.RecordCustomNoteChanges ( newNote , oldNote , GRM_G.addonUser , playerName , true )
        -- Update the note live!
        if GRM_G.currentName == playerName then
            GRM_UI.GRM_MemberDetailMetaData.GRM_CustomNoteEditBoxFrame.GRM_CustomNoteEditBox:SetText ( newNote );
        end
    end
end

-- Method:          GRM.IsAnyCustomNoteLarge()
-- What it Does:    Returns true if at least one guildie as a custom note > 100
-- Purpose:         Adjust the throttle cap.
GRM.IsAnyCustomNoteLarge = function()
    local result = false;
    local guildData = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ];

    for _ , player in pairs ( guildData ) do
        if type ( player ) == "table" then
            if #player.customNote[6] > 99 then
                result = true;
                break;
            end
        end
    end
    return result;
end

-- Method:          GRM.IsAnyAltActive ( table , int)
-- What it Does:    Returns true if at least one of the alts is active
-- Purpose:         No need to announce inactive return if it is just an old alt...
GRM.IsAnyAltActive = function ( listOfAlts , hours )
    local result = false;
    local inactiveHours = hours or GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].inactiveHours;
    local guildData = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ];
    local player;

    for i = 1 , #listOfAlts do
        player = guildData[listOfAlts[i][1]];
        if player ~= nil then
            if player.lastOnline < inactiveHours then
                result = true;
                break;
            end
        end
    end

    return result;
end

-- Method:          GRM.IsAnyAltActiveForRecommendKicks ( table , rule  )
-- What it Does:    Returns true if at least one of the alts is active
-- Purpose:         No need to announce inactive return if it is just an old alt...
GRM.IsAnyAltActiveForRecommendKicks = function ( listOfAlts , ruleName )
    local result = false;
    local guildData = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ];
    local player;

    for i = 1 , #listOfAlts do
        player = guildData[listOfAlts[i][1]];
        if player ~= nil then
            if player.lastOnline < GRM_G.NumberOfHoursTilRecommend.kick[ruleName] then
                result = true;
                break;
            end
        end
    end

    return result;
end

-- Method           GRM.RecordChanges( int , table , table )
-- What it does:    Builds all the changes, sorts them, then adds them to change report
-- Purpose:         Consolidation of data for final output report.
GRM.RecordChanges = function ( indexOfInfo , member , memberOldInfo , logEntryMetaData , dateArray )
    if GRM_G.changeHappenedExitScan or not IsInGuild() then
        GRM.ResetTempLogs();
        GRM_G.changeHappenedExitScan = false;
        GRM_G.CurrentlyScanning = false;
        return;
    end
    local simpleName = "";
    if type ( member ) == "string" then
        simpleName = GRM.GetStringClassColorByName ( member ) .. GRM.SlimName ( member ) .. "|r";
    elseif indexOfInfo ~= 10 then
        simpleName = GRM.GetStringClassColorByName ( member.name ) .. GRM.SlimName ( member.name ) .. "|r";
    else
        simpleName = GRM.GetClassColorRGB ( member.class , true ) .. GRM.SlimName ( member.name ) .. "|r";
    end

    -- 2 = Guild Rank Promotion
    if indexOfInfo == 2 then
        local goodToAdd = true;
        if logEntryMetaData[1] then

            -- Ok, ranks are same name - does this mean the rank itself has shifted, or that there are ranks with the same name?
            -- Well, let's see if there are 2 ranks the same name..
            if logEntryMetaData[4] == logEntryMetaData[5] then
                local c = 0;
                for i = 1 ,  GuildControlGetNumRanks() do
                    if GuildControlGetRankName ( i ) == logEntryMetaData[4] then
                        c = c + 1;
                    end
                end

                if c == 1 then
                    goodToAdd = false;
                end
            end
            if goodToAdd then
                GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , logEntryMetaData[2] , logEntryMetaData[3] , logEntryMetaData[4] , logEntryMetaData[5] , logEntryMetaData[6][3] );
            end
        else
            if memberOldInfo.rankName == member.rankName then
                local c = 0;
                for i = 1 ,  GuildControlGetNumRanks() do
                    if GuildControlGetRankName ( i ) == memberOldInfo.rankName then
                        c = c + 1;
                    end
                end

                if c == 1 then
                    goodToAdd = false;      -- This means there aren't multiple of same rank
                end
            end


            if goodToAdd then
                GRM.AddPromotionDateTempLogEntry ( logEntryMetaData[1] , nil , simpleName , memberOldInfo.rankName , member.rankName , dateArray );
            end
        end

    -- 9 = Guild Rank Demotion
    elseif indexOfInfo == 9 then

        local goodToAdd = true;
        if logEntryMetaData[1] then

            -- Ok, ranks are same name - does this mean the rank itself has shifted, or that there are ranks with the same name?
            -- Well, let's see if there are 2 ranks the same name..
            if logEntryMetaData[4] == logEntryMetaData[5] then
                local c = 0;
                for i = 1 ,  GuildControlGetNumRanks() do
                    if GuildControlGetRankName ( i ) == logEntryMetaData[4] then
                        c = c + 1;
                    end
                end

                if c == 1 then
                    goodToAdd = false;
                end
            end

            if goodToAdd then
                GRM.AddDemotionDateTempLogEntry ( logEntryMetaData[1] , logEntryMetaData[2] , logEntryMetaData[3] , logEntryMetaData[4] , logEntryMetaData[5] , logEntryMetaData[6][3] );
            end
        else
            if memberOldInfo.rankName == member.rankName then
                local c = 0;
                for i = 1 ,  GuildControlGetNumRanks() do
                    if GuildControlGetRankName ( i ) == memberOldInfo.rankName then
                        c = c + 1;
                    end
                end

                if c == 1 then
                    goodToAdd = false;
                end
            end

            if goodToAdd then
                GRM.AddDemotionDateTempLogEntry ( logEntryMetaData[1] , nil , simpleName , memberOldInfo.rankName , member.rankName , dateArray );
            end
        end

    -- 4 = level
    elseif indexOfInfo == 4 then
        GRM.AddLeveledTempLogEntry ( simpleName , logEntryMetaData , member.level , ( member.level - memberOldInfo.level ) , dateArray , ( member.level == GRM_G.LvlCap ) , member.name );

    -- 5 = note
    elseif indexOfInfo == 5 then
        GRM.AddNoteTempLogEntry ( simpleName , memberOldInfo.note , member.note , dateArray )
    -- 6 = officerNote
    elseif indexOfInfo == 6 then
        GRM.AddOfficerNoteTempLogEntry ( simpleName , memberOldInfo.officerNote , member.officerNote , dateArray );

        -- 8 = Guild Rank Name Changed to something else
    elseif indexOfInfo == 8 then
        GRM.AddRankRenameEntry ( nil , memberOldInfo.rankName , member.rankName , dateArray );

    -- 10 = New Player
    elseif indexOfInfo == 10 then
        -- Check against old member list first to see if returning player!
        GRM.RecordJoinChanges ( member , simpleName , false , dateArray );

    -- 11 = Player Left  
    elseif indexOfInfo == 11 then
        table.insert ( GRM_G.TempLeftGuildPlaceholder , { member.name , simpleName , false , dateArray } );

    -- 12 = NameChanged
    elseif indexOfInfo == 12 then
        local colorHex = GRM.GetClassHex ( memberOldInfo.class );
        GRM.AddNameChangeTempLogEntry ( ( colorHex .. simpleName .. "|r" ) , ( colorHex .. GRM.SlimName (  memberOldInfo.name ) .. "|r" ) , dateArray );

    -- 13 = Inactive Members Return!
    elseif indexOfInfo == 13 then
        GRM.AddInactiveReturnTempLogEntry ( simpleName , GRM.HoursReport ( memberOldInfo ) , dateArray );
    end
end

-- Method:          GRM.AddonPlayerRankChange ( index )
-- What it Does:    If the player's rank is changed, it does a re-check on permissions and sets up sync again.
-- purpose:         Need to update personal details immediately so sync can be processed.
GRM.AddonPlayerRankChange = function( newRankIndex )

    local needsUpdate = false;
    if newRankIndex < GRM_G.playerRankID then
        needsUpdate = true;
    end

    GRM_G.playerRankID = newRankIndex;

    GRM_UI.BuildLogFrames();
    
    -- Determine if player has access to guild chat or is in restricted chat rank - need to recheck with rank change.
    GRM_G.HasAccessToOfficerChat = false;
    GRM.RegisterGuildChatPermission();

    if GRM_UI.GRM_ToolCoreFrame ~= nil and GRM_UI.GRM_ToolCoreFrame:IsVisible() then
        if needsUpdate then
            GRM.Report ( GRM.L ( "GRM:" ) .. " " .. GRM.L ( "Player rank change detected, re-checking permissions and rebuilding GRM Macro Tool." ) );
        end
        GRM_UI.RefreshManagementTool();
    end

    -- Let's do a resync check as well... If permissions have changed, we should resync check em.
    -- First, RESET all..
    if needsUpdate and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].syncEnabled and not GRMsyncGlobals.currentlySyncing and GRM_G.HasAccessToGuildChat and not GRM_G.OnFirstLoad then
        if GRMsync.IsPlayerDataSyncCompatibleWithAnyOnline() then
            GRMsync.TriggerFullReset();
            GRM.Report ( GRM.L ( "GRM:" ) .. " " .. GRM.L ( "Your rank has changed. Re-evaluating sync permissions." ) );
            C_Timer.After ( 3 , GRMsync.Initialize ); -- Now, let's add a brief delay, 3 seconds, to trigger sync again
        end
    end
end

-- Method:          GRM.CheckPlayerChanges ( array )
-- What it Does:    Scans through guild roster and re-checks for any  (Will only fire if guild is found!)
-- Purpose:         Keep whoever uses the addon in the know instantly of what is going and changing in the guild.
GRM.CheckPlayerChanges = function ( roster )

    -- Extra logic to prevent dupliated scans
    if not ( GRM_G.ManualScanEnabled and not GRM_UI.GRM_ToolCoreFrame.MacroSuccess ) and ( ( GRM_G.CurrentlyScanning ) or ( ( time() - GRM_G.ScanRosterTimer ) < GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].scanDelay ) ) then
        return;
    else
        GRM_G.ScanRosterTimer = time();
    end

    if not GRM_G.CurrentlyScanning then
        GRM_G.CurrentlyScanning = true;
    else
        return;
    end
    if GRM_G.changeHappenedExitScan or not IsInGuild() then    -- This provides an escape if the player quits the guild in the middle of the scan process, or on first joining, to avoid lua error then
        GRM.ResetTempLogs();
        GRM_G.changeHappenedExitScan = false;
        GRM_G.CurrentlyScanning = false;
        return;
    end

    local newPlayerFound;
    local guildData = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ];
    local player;

    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].scanEnabled or GRM_G.ManualScanEnabled then

        for x in pairs ( roster ) do
            newPlayerFound = true;
            player = guildData[x];

            if player ~= nil then
                newPlayerFound = false;
                -- No need to do any of this if they haven't logged in... the only potential change necessary to check would be public/officer note as that is the only thing that can change when offline.
                -- This boolean is all about massive resource saving
                -- if online, or hours since last logged is different or public note is different or officer note is different and you can read it.
                if roster[x].isOnline or roster[x].rankName ~= player.rankName or roster[x].rankIndex ~= player.rankIndex or roster[x].level ~= player.level or roster[x].note ~= player.note or roster[x].class ~= player.class or ( roster[x].officerNote ~= player.officerNote and GRM.CanViewOfficerNote() ) then

                    for k = 2 , 7 do
                        if k < 7 then -- CHANGE FOUND! New info and old info are not equal!
                            -- Ranks
                            if k == 3 and roster[x].rankIndex ~= player.rankIndex then -- This checks to see if guild just changed the name of a rank.

                                local added = false;
                                local timestamp , dateArray = GRM.GetTimestamp();
                                local epochTime = time();
                                local logEntryroster = {};

                                if roster[x].rankName ~= player.rankName or ( roster[x].rankName == player.rankName and not GRM_G.numRanksHasChanged ) then
                                    -- Promotion Obtained                                
                                    if roster[x].rankIndex < player.rankIndex then

                                        added , logEntryroster = GRM.GetGuildEventString ( 2 , roster[x].name , player.rankName , roster[x].rankName );
                                        if added then
                                            timestamp = logEntryroster[6][1];
                                            epochTime = logEntryroster[6][2];
                                            dateArray = logEntryroster[6][3];
                                        end
                                        GRM.RecordChanges ( 2 , roster[x] , player , logEntryroster , dateArray );
                                        
                                    -- Demotion Obtained
                                    elseif roster[x].rankIndex > player.rankIndex then
                                        added , logEntryroster = GRM.GetGuildEventString ( 1 , roster[x].name , player.rankName , roster[x].rankName );
                                        if added then
                                            timestamp = logEntryroster[6][1];
                                            epochTime = logEntryroster[6][2];
                                            dateArray = logEntryroster[6][3];
                                        end
                                        
                                        if roster[x].rankName ~= player.rankName then
                                            GRM.RecordChanges ( 9 , roster[x] , player , logEntryroster , dateArray );
                                        end
                                    end
                                    
                                    player.rankName = roster[x].rankName; -- Saving new rank Info
                                    player.rankIndex = roster[x].rankIndex; -- Saving new rank Index Info

                                    -- Clear if set to unknown
                                    player.promoteDateUnknown = false;

                                    -- For SYNC
                                    if added then
                                        player.verifiedPromoteDate[1] = timestamp;
                                        player.verifiedPromoteDate[2] = epochTime;
                                    end
                                    

                                    if roster[x].rankName ~= player.rankName then
                                        if player.rankHistory[1][3] == 0 then
                                            player.rankHistory[1][1] = player.rankName;
                                            player.rankHistory[1][2] = string.sub ( timestamp , 1 , string.find ( timestamp , "'" ) + 2 )
                                            player.rankHistory[1][3] = epochTime;
                                        else
                                            table.insert ( player.rankHistory , { player.rankName , string.sub ( timestamp , 1 , string.find ( timestamp , "'" ) + 2 ) , epochTime } ); -- New rank, date, metatimestamp
                                        end
                                    else
                                        player.rankHistory[#player.rankHistory][1] = player.rankName;
                                        player.rankHistory[#player.rankHistory][2] = string.sub ( timestamp , 1 , string.find ( timestamp , "'" ) + 2 );
                                        player.rankHistory[#player.rankHistory][3] = epochTime;
                                    end
                                    
                                    -- Update the player index if it is the player themselves that received the change in rank.
                                    if roster[x].name == GRM_G.addonUser then

                                        GRM.AddonPlayerRankChange ( roster[x].rankIndex );
                                        
                                    end

                                -- Adjust new rank index - make rank change good.
                                elseif GRM_G.numRanksHasChanged and roster[x].rankName == player.rankName then
                                    player.rankIndex = roster[x].rankIndex; -- Saving new rank Index Info
                                end

                            elseif k == 2 and roster[x].rankName ~= player.rankName and roster[x].rankIndex == player.rankIndex then

                                player.rankName = roster[x].rankName; -- Saving new Info
                                if player.rankHistory[1][3] > 0 then
                                    player.rankHistory[#player.rankHistory][1] = roster[x].rankName;   -- Adjusting the historical name if 
                                    -- We are just renaming the rank in the rankHistory - nothing else.
                                end

                            -- Level
                            elseif k == 4 and roster[x].level ~= player.level then
                                if roster[x].level > player.level and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].recordLevelUp then
                                    local needsToRecord = false;
                                    local mileStones = { 10 , 20 , 30 , 40 , 50 };
                                    local level = 0;

                                    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].levelReportMin <= roster[x].level then
                                        needsToRecord = true
                                    else
                                        for i = ( #mileStones ) , 1 , -1 do
                                            if roster[x].level >= mileStones[i] and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].levelFilters[i] and player.level < mileStones[i] then
                                                needsToRecord = true;
                                                level = mileStones[i];
                                                break;
                                            end
                                        end
                                    end
                                    if needsToRecord then
                                        GRM.RecordChanges ( 4 , roster[x] , player , level , select ( 2 , GRM.GetTimestamp() ) );
                                    end
                                end
                                player.level = roster[x].level; -- Saving new Info

                            -- Note
                            elseif k == 5 then
                                -- Let's first compare the notes... make sure they are not the same
                                local isDifferent = true;
                                local newNote , oldNote = "" , "";

                                if GetCVar("profanityFilter") == "1" then                               -- The profanity filter is on!
                                    newNote = GRM.NormalizeMatureWords ( roster[x].note );
                                    oldNote = GRM.NormalizeMatureWords ( player.note );
                                else
                                    newNote = roster[x].note;
                                    oldNote = player.note;
                                end

                                if newNote == oldNote then
                                    isDifferent = false;
                                end

                                if isDifferent then
                                    GRM.RecordChanges ( 5 , roster[x] , player , nil , select ( 2 , GRM.GetTimestamp() ) );
                                    player.note = roster[x].note;
                                    -- Update metaframe
                                    if GRM_UI.GRM_MemberDetailroster ~= nil and GRM_UI.GRM_MemberDetailroster:IsVisible() and GRM_G.currentName == roster[x].name then
                                        if roster[x].note == "" then
                                            if ( CanEditPublicNote() or ( GRM_G.BuildVersion >= 80000 and GRM_G.currentName == GRM_G.addonUser ) ) then
                                                GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString1:SetText ( GRM.L ( "Click here to set a Public Note" ) );
                                            else
                                                GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString1:SetText ( GRM.L ( "Unable to Edit Public Note at Rank" ) );
                                            end
                                        else
                                            GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString1:SetText ( roster[x].note );
                                        end
                                        GRM_UI.GRM_MemberDetailroster[x].GRM_PlayerNoteEditBox:SetText ( roster[x].note );
                                    end
                                end

                            -- Officer Note
                            elseif k == 6 and GRM.CanViewOfficerNote() then
                                if roster[x].officerNote == nil or player.officerNote == nil then
                                    player.officerNote = roster[x].officerNote;
                                else
                                    local isDifferent = true;
                                    local newNote , oldNote = "" , "";

                                    if GetCVar("profanityFilter") == "1" then                               -- The profanity filter is on!
                                        newNote = GRM.NormalizeMatureWords ( roster[x].officerNote );
                                        oldNote = GRM.NormalizeMatureWords ( player.officerNote );
                                    else
                                        newNote = roster[x].officerNote;
                                        oldNote = player.officerNote;
                                    end

                                    if newNote == oldNote then
                                        isDifferent = false;
                                    end

                                    if isDifferent then
                                        -- Guild Leader! Let's check their officer note for changes for settings!
                                        GRM.RecordChanges ( 6 , roster[x] , player , nil , select ( 2 , GRM.GetTimestamp() ) );
                                        player.officerNote = roster[x].officerNote;
                                    end
                                end
                                if GRM_UI.GRM_MemberDetailroster ~= nil and GRM_UI.GRM_MemberDetailroster:IsVisible() and GRM_G.currentName == roster[x].name then
                                    if roster[x].officerNote == "" then
                                        if GRM.CanEditOfficerNote() then
                                            GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString2:SetText ( GRM.L ( "Click here to set an Officer's Note" ) );
                                        else
                                            GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString2:SetText ( GRM.L ( "Unable to Edit Officer Note at Rank" ) );
                                        end
                                    else
                                        GRM_UI.GRM_MemberDetailroster[x].GRM_noteFontString2:SetText ( roster[x].officerNote );
                                    end
                                    GRM_UI.GRM_MemberDetailroster[x].GRM_PlayerOfficerNoteEditBox:SetText (  roster[x].officerNote );
                                end
                            end

                        -- Class Change - fix error or future class change redundancy if they ever implemented
                        elseif k == 7 and roster[x].class ~= player.class then
                            player.class = roster[x].class;

                            -- TO DO IF EVER IMPLEMENTED
                            -- UPDATE THE ALT INFO CLASS AS WELL IF NEED TO CHANGE.
                        end
                    end
                end

                -- Recommendations for Offline Kicks
                if roster[x].lastOnline ~= -1 then

                    -- Report inactive returns
                    if GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].reportInactiveReturn and player.lastOnline > GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].inactiveHours and roster[x].lastOnline < GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].inactiveHours and player.lastOnline > roster[x].lastOnline then  -- Player has logged in after having been inactive for greater than given time
                        local needsToReport = true;

                        -- No need to report if any player's alts are active still...
                        if #player.alts > 0 and GRM_AddonSettings_Save[GRM_G.F][GRM_G.addonUser].allAltRequirement then
                            if GRM.IsAnyAltActive ( player.alts ) then
                                needsToReport = false;
                            end
                        end

                        if needsToReport then
                            GRM.RecordChanges ( 13 , roster[x].name , ( player.lastOnline - roster[x].lastOnline ) , nil , select ( 2 , GRM.GetTimestamp() ) );   -- Recording the change in hours to log
                        end
                    end

                    player.lastOnline = roster[x].lastOnline;                   -- Set new hours since last login.
                end

                -- Just straight update these everytime... No need for change check
                if ( roster[x].isOnline and player.zone ~= roster[x].zone ) or GRM_G.OnFirstLoad then
                    player.timeEnteredZone = time();   -- Resetting the time on hitting this zone.
                end
                player.zone = roster[x].zone;                               -- zone
                player.achievementPoints = roster[x].achievementPoints;     -- Achievement pts
                player.isMobile = roster[x].isMobile;                       -- isMobile
                player.guildRep = roster[x].rep;                            -- Guild Reputation
                player.isOnline = roster[x].isOnline;                       -- online Status
                player.status = roster[x].status;                           -- Active Status
                player.GUID = roster[x].GUID;                               -- GUID

                if roster[x].race ~= "" then
                    player.race = roster[x].race;    -- Race
                end
                if roster[x].sex ~= "" then
                    player.sex = roster[x].sex;    -- Sex
                end
            end

            -- NEW PLAYER FOUND! (Maybe)
            if newPlayerFound then
                table.insert ( GRM_G.newPlayers , roster[x] );
            end
        end

        -- Checking if any players left the guild
        C_Timer.After ( 1 , function()
            if GRM_G.changeHappenedExitScan or not IsInGuild() then
                GRM.ResetTempLogs();
                GRM_G.changeHappenedExitScan = false;
                GRM_G.CurrentlyScanning = false;
                return;
            end

            -- Scan the roster for missing names...
            for _ , member in pairs ( guildData ) do
                if type ( member ) == "table" then
                    if roster[member.name] == nil then         -- It exists in current database, but the new one it is missing, thus implying player is no longer in the guild.
                        table.insert ( GRM_G.leavingPlayers , member );
                    end
                end
            end

            -- Spread out the scans to avoid stutter...
            C_Timer.After ( 1 , function()
                if GRM_G.changeHappenedExitScan or not IsInGuild() then
                    GRM.ResetTempLogs();
                    GRM_G.changeHappenedExitScan = false;
                    GRM_G.CurrentlyScanning = false;
                    return;
                end

                -- Final check on players that left the guild to see if they are namechanges.GRM.CanViewOfficerNote
                local playerNotMatched = true;
                if #GRM_G.leavingPlayers > 0 and #GRM_G.newPlayers > 0 then
                    for k = 1 , #GRM_G.leavingPlayers do
                        playerNotMatched = true;
                        for j = 1 , #GRM_G.newPlayers do
                            if ( GRM_G.leavingPlayers[k] ~= nil and GRM_G.newPlayers[j] ~= nil ) and GRM_G.leavingPlayers[k].GUID == GRM_G.newPlayers[j].GUID then   -- COMPARING GUID
                                playerNotMatched = false;   -- In other words, player was found, but it's a namechange!!!!!
                                -- Match Found!!!
                                GRM.RecordChanges ( 12 , GRM_G.newPlayers[j] , GRM_G.leavingPlayers[k] , nil , select ( 2 , GRM.GetTimestamp() ) );

                                -- We need to change the name EVERYWHERE!!!
                                local tempGuild = guildData;
                                for member in pairs ( tempGuild ) do
                                    if type ( tempGuild[member] ) == "table" then
                                        if GRM_G.leavingPlayers[k].GUID == tempGuild[member].GUID then -- Mathching the Leaving player to historical index so it can be identified and new name stored.
                                            -- Need to remove him from list of alts IF he has a lot of alts...
                                            if #tempGuild[member].alts > 0 then

                                                local listOfAlts = tempGuild[member].alts;                      -- Found the match, now grabbing list of all alts
                                                local tempAlt;
                                                for m = 1 , #listOfAlts do                                      -- Cycling through full list
                                                    tempAlt = tempGuild[listOfAlts[m][1]];
                                                    if tempAlt ~= nil then
                                                        for t = 1 , #tempAlt.alts do
                                                            if tempAlt.alts[t][1] == GRM_G.leavingPlayers[k].name then
                                                                tempAlt.alts[t][1] = GRM_G.newPlayers[j].name;
                                                                break;
                                                            end
                                                        end
                                                    end
                                                end
                                            end

                                            -- Need to change the name of the players too
                                            for s in pairs ( tempGuild ) do
                                                if type ( tempGuild[s] ) == "table" then
                                                    if #tempGuild[s].removedAlts > 0 then
                                                        for t = 1 , #tempGuild[s].removedAlts do
                                                            if tempGuild[s].removedAlts[t][1] == GRM_G.leavingPlayers[k].name then
                                                                tempGuild[s].removedAlts[t][1] = GRM_G.newPlayers[j].name;
                                                                break;
                                                            end
                                                        end
                                                    end
                                                end
                                            end

                                            -- Group Info module -- update player Name change as well
                                            if GRM_GroupInfo_Save ~= nil then
                                                GRM_GroupInfo_Save[GRM_G.newPlayers[j].name] = {};
                                                GRM_GroupInfo_Save[GRM_G.newPlayers[j].name] = GRM.DeepCopyArray ( GRM_GroupInfo_Save[GRM_G.leavingPlayers[k].name] );
                                                GRM_GroupInfo_Save[GRM_G.leavingPlayers[k].name] = nil;
                                            end

                                            tempGuild[GRM_G.newPlayers[j].name] = {};
                                            tempGuild[GRM_G.newPlayers[j].name] = GRM.DeepCopyArray ( tempGuild[GRM_G.leavingPlayers[k].name] );
                                            tempGuild[GRM_G.newPlayers[j].name].name = GRM_G.newPlayers[j].name;            -- Need to update this new name too!
                                            tempGuild[GRM_G.leavingPlayers[k].name] = nil;
                                            break
                                        end
                                    end
                                end
                                -- since namechange identified, also need to remove name from GRM_G.newPlayers array now.
                                if #GRM_G.newPlayers == 1 then
                                    GRM_G.newPlayers = {}; -- Clears the array of the one name.
                                else
                                    table.remove ( GRM_G.newPlayers , j );
                                end
                                guildData = tempGuild;  -- redundant?
                                break;
                            end
                        end
                        
                        -- Player not matched! For sure this player has left the guild!
                        if playerNotMatched then
                            GRM.RecordChanges ( 11 , GRM_G.leavingPlayers[k] , GRM_G.leavingPlayers[k] , nil , select ( 2 , GRM.GetTimestamp() ) );
                        end
                    end
                elseif #GRM_G.leavingPlayers > 0 then
                    for k = 1 , #GRM_G.leavingPlayers do
                        GRM.RecordChanges ( 11 , GRM_G.leavingPlayers[k] , GRM_G.leavingPlayers[k] , nil , select ( 2 , GRM.GetTimestamp() ) );
                    end
                end
                if #GRM_G.newPlayers > 0 then
                    for k = 1 , #GRM_G.newPlayers do
                        GRM.RecordChanges ( 10 , GRM_G.newPlayers[k] , GRM_G.newPlayers[k] , nil , select ( 2 , GRM.GetTimestamp() ) );
                    end
                end

                -- Now that we have collected all the players to be kicked... Let's not spam the log with alt info by parsing it properly.
                if #GRM_G.TempLeftGuildPlaceholder > 0 then
                    for k = 1 , #GRM_G.TempLeftGuildPlaceholder do
                        GRM.AddLeftOrKickEntry ( GRM.RecordKickChanges ( GRM_G.TempLeftGuildPlaceholder[k][1] , GRM_G.TempLeftGuildPlaceholder[k][3] , GRM_G.TempLeftGuildPlaceholder[k][4] ) );
                    end
                end

                -- OK, let's close this out!!!!!
                C_Timer.After ( 1 , function()
                    -- Seeing if any upcoming notable events, like anniversaries/birthdays
                    GRM.CheckPlayerEvents();

                    -- Printing Report, and sending report to log.
                    GRM.FinalReport();

                    -- Disable manual scan if activated.
                    if GRM_G.ManualScanEnabled and GRM_UI.GRM_ToolCoreFrame.MacroSuccess then
                        GRM_G.ManualScanEnabled = false;
                        GRM.Report ( GRM.L ( "GRM:" ) .. " " .. GRM.L ( "Manual Scan Complete" ) , 1.0 , 0.84 , 0 );
                    end

                    if GRM_G.ManualScanEnabled and not GRM_UI.GRM_ToolCoreFrame.MacroSuccess then
                        GRM_G.ManualScanEnabled = false;
                        GRM.ValidateMacroRecordingSuccess ( true );
                    end

                end);
            end);
        end);
    else
        -- Seeing if any upcoming notable events, like anniversaries/birthdays
        GRM.CheckPlayerEvents();

        -- Printing Report, and sending report to log.
        GRM.FinalReport();

        -- Disable manual scan if activated.
        if GRM_G.ManualScanEnabled and GRM_UI.GRM_ToolCoreFrame.MacroSuccess then
            GRM_G.ManualScanEnabled = false;
            GRM.Report ( GRM.L ( "GRM:" ) .. " " .. GRM.L ( "Manual Scan Complete" ) , 1.0 , 0.84 , 0 );
        end
    end
end

-- Method:          GRM.GuildNameChanged( string )
-- What it Does:    Returns true if the player's guild is the same, it just changed its name
-- Purpose:         Good to know... what a pain it would be if you had to reset all of your settings
GRM.GuildNameChanged = function ( currentGuildName )
    local result = false;

        -- If the guild name is found in database but club IDs do not match, then it can be determined that this is likely a namechange, but it is coming from a guild someone was just holding on to.
    if GRM_GuildMemberHistory_Save[ GRM_G.F ][currentGuildName] == nil or ( GRM_GuildMemberHistory_Save[ GRM_G.F ][currentGuildName] ~= nil and GRM_GuildMemberHistory_Save[ GRM_G.F ][currentGuildName].grmClubID ~= GRM_G.gClubID ) then
    -- For each guild
        for guild in pairs ( GRM_GuildMemberHistory_Save[ GRM_G.F ] ) do
            if GRM_GuildMemberHistory_Save[ GRM_G.F ][guild].grmClubID == GRM_G.gClubID then
                result = true;

                GRM_PlayersThatLeftHistory_Save[GRM_G.F][currentGuildName] = {};
                GRM_PlayersThatLeftHistory_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_PlayersThatLeftHistory_Save[GRM_G.F][guild] );
                GRM_PlayersThatLeftHistory_Save[GRM_G.F][guild] = nil;

                GRM_GuildMemberHistory_Save[GRM_G.F][currentGuildName] = {};
                GRM_GuildMemberHistory_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_GuildMemberHistory_Save[GRM_G.F][guild] );
                GRM_GuildMemberHistory_Save[GRM_G.F][currentGuildName].grmName = currentGuildName;
                GRM_GuildMemberHistory_Save[GRM_G.F][guild] = nil;

                GRM_PlayerListOfAlts_Save[GRM_G.F][currentGuildName] = {};
                GRM_PlayerListOfAlts_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_PlayerListOfAlts_Save[GRM_G.F][guild] );
                GRM_PlayerListOfAlts_Save[GRM_G.F][guild] = nil;

                GRM_LogReport_Save[GRM_G.F][currentGuildName] = {};
                GRM_LogReport_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_LogReport_Save[GRM_G.F][guild] );
                GRM_LogReport_Save[GRM_G.F][guild] = nil;

                GRM_CalendarAddQue_Save[GRM_G.F][currentGuildName] = {};
                GRM_CalendarAddQue_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_CalendarAddQue_Save[GRM_G.F][guild] );
                GRM_CalendarAddQue_Save[GRM_G.F][guild] = nil;

                -- Also need to change the guild's name in the saved database...
                for x in pairs ( GRM_GuildDataBackup_Save[GRM_G.F][guild] ) do
                    if type ( x ) == "string" then
                        if #GRM_GuildDataBackup_Save[GRM_G.F][guild][x] > 0 then
                            GRM_GuildDataBackup_Save[GRM_G.F][guild][x]["members"].grmName = currentGuildName;
                            GRM_GuildDataBackup_Save[GRM_G.F][guild][x]["formerMembers"].grmName = currentGuildName;
                        end
                    end
                end

                GRM_GuildDataBackup_Save[GRM_G.F][currentGuildName] = {};
                GRM_GuildDataBackup_Save[GRM_G.F][currentGuildName] = GRM.DeepCopyArray ( GRM_GuildDataBackup_Save[GRM_G.F][guild] );
                GRM_GuildDataBackup_Save[GRM_G.F][guild] = nil;
                break;
            end
        end
    end
    return result;
end

-- Method:          GRM_UI.ValidateUnverifiedDate ( table )
-- What it Does:    Checks if formatting is valid, then if not it fixes it
-- Purpose:         Cleanup some old formatting bugs with old data. Added redundency as well.
GRM.ValidateUnverifiedDate = function( player , dateVersion )

    -- If general formatting error, replace.
    if not player.rankHistory or #player.rankHistory == 0 or player.rankHistory[1][3] == 0 then
        player.rankHistory = { {  "" , "" , 0 } };
    end

    if not player[dateVersion] or #player[dateVersion] == 0 then
        player[dateVersion] = { "" , 0 };
    end

    local toRemove = false;

    for i = #player.rankHistory , 1 , -1 do

        -- add generic table if necessary
        if not type (player.rankHistory[i] ) == "table" then
            player.rankHistory[i] = {  "" , "" , 0 };

        elseif ( not player.rankHistory[i][1] or type ( player.rankHistory[i][1] ) ~= "string" ) or ( not player.rankHistory[i][2] or type ( player.rankHistory[i][2] ) ~= "string" ) or ( not player.rankHistory[i][3] or type ( player.rankHistory[i][1] ) ~= "number" ) then

            if i == #player.rankHistory then

                if player[dateVersion][1] ~= "" then
                    player.rankHistory[i][1] = player.rankName;
                    player.rankHistory[i][2] = GRM.GetCleanTimestamp ( player[dateVersion][1] );
                    player.rankHistory[i][3] = GRM.TimeStampToEpoch ( player.rankHistory[i][2] , true );
                end
            else
                -- if one is errored they all are.
                toRemove = false;

                -- We must remov
                if ( not player.rankHistory[i][1] or type ( player.rankHistory[i][1] ) ~= "string" ) or ( not player.rankHistory[i][2] or type ( player.rankHistory[i][2] ) ~= "string" ) then
                    toRemove = true;

                elseif ( not player.rankHistory[i][3] or type ( player.rankHistory[i][3] ) ~= "number" ) then

                    if player.rankHistory[i][2] ~= "" then
                        player.rankHistory[i][3] = GRM.TimeStampToEpoch ( GRM.GetCleanTimestamp ( player.rankHistory[i][2] ) , true );
                    else
                        toRemove = true;
                    end

                end

                if toRemove then
                    table.remove ( player.rankHistory , i );
                end
            end 
        end
    end

    return player;
end

-- Method:          GRM.ParseGuildRanks()
-- What it Does:    Parses the string with al the guild ranks and returns it as an array
-- Purpose:         Due to some tables scanning logic I want to keep this stored as a string and then converted to a table during your session or re-converted if the ranks are updated.
GRM.ParseGuildRanks = function()
    local ranks = {};

    for rankName in string.gmatch ( GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].ranks , "[^||]+" ) do
        table.insert ( ranks , rankName );
    end
    return ranks;
end

-- Method:          GRM.CheckGuildRanks()
-- What it Does:    Checks for any changes in the guild rank structure of the guild and reports on them
-- Purpose:         Just extra info, especially to help make it more clear to the player why they might get spammed in their log for mass demote/promotions
GRM.CheckGuildRanks = function()
    -- If the ranks are set, let's check if they do not match now.
    local numRanks = GuildControlGetNumRanks();
    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].ranks = GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].ranks or GRM.GetListOfGuildRanks ( true , true , true );
    
    GRM_G.guildRankNames = GRM_G.guildRankNames or GRM.ParseGuildRanks();

    if GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks == nil or GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks == 0 then
        GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks = numRanks or 0;
    else
        if numRanks ~= GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks then
            
            GRM.AddRankRenameEntry ( ( numRanks - GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks ) , nil , nil , select ( 2 , GRM.GetTimestamp() ) );
            GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].grmNumRanks = numRanks;
            GRM_G.numRanksHasChanged = true;

        else
            
            local rankNames = GRM.GetListOfGuildRanks ( true , true );
            local changeMade = false;

            for i = 1 , #rankNames do
                if rankNames[i] ~= GRM_G.guildRankNames[i] then
                    changeMade = true;
                    GRM.AddRankRenameEntry ( nil , GRM_G.guildRankNames[i] , rankNames[i] , select ( 2 , GRM.GetTimestamp() ) );
                end
            end

            if changeMade then
                GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ].ranks = GRM.GetListOfGuildRanks ( true , true , true );
                GRM_G.guildRankNames = GRM.ParseGuildRanks();
            end
        end
    end        
end

-- Method:          GRM.BuildNewRoster()
-- What it does:    Rebuilds the roster to check against for any changes.
-- Purpose:         To track for guild changes of course!
GRM.BuildNewRoster = function()
    local roster = {};
    -- Checking if Guild Found or Not Found, to pre-check for Guild name tag.
    local guildNotFound = false;
    if not GRM_GuildMemberHistory_Save[GRM_G.F][GRM_G.guildName] then
        guildNotFound = true;
    end

    local atLeastOne = false;

    for i = 1 , GRM.GetNumGuildies() do
        -- For guild info
        local name , rank , rankInd , level , _ , zone , note , officerNote , online , status , class , achievementPoints , _ , isMobile , _ , rep , GUID = GetGuildRosterInfo ( i );

        roster[name] = {};      -- For easy referencing.
        roster[name].name = name                                    -- 1
        roster[name].rankName = rank;                               -- 2
        roster[name].rankIndex = rankInd;                           -- 3
        roster[name].level = level;                                 -- 4
        roster[name].note = note;                                   -- 5
        if GRM.CanViewOfficerNote() then -- Officer Note permission to view.
            roster[name].officerNote = officerNote;                 -- 6
        else
            roster[name].officerNote = nil; -- Set Officer note to nil if needed due to player not being able to view. - If it is set to "" then player will think it is changing.
        end
        roster[name].class = class;                                 -- 7
        roster[name].lastOnline = GRM.GetHoursSinceLastOnline ( i , online ); -- 8 Time since they last logged in in hours.
        roster[name].zone = zone;                                   -- 9
        roster[name].achievementPoints = achievementPoints;         -- 10
        roster[name].isMobile = isMobile;                           -- 11
        roster[name].rep = rep;                                     -- 12
        roster[name].isOnline = online;                             -- 13
        roster[name].status = status;                               -- 14
        roster[name].GUID = GUID;                                   -- 15

        local race , sex = select ( 4 , GetPlayerInfoByGUID ( GUID ) );
        if race == nil or sex == nil then
            race , sex = select ( 4 , GetPlayerInfoByGUID ( GUID ) );   -- Call a second time... sometimes the server is weird and the first call produces nil, but the immediate 2nd does respond.
            if race == nil or sex == nil then
                race = "";
                sex = 1;
            end
        end

        roster[name].race = race;                                   -- 16
        roster[name].sex = sex;                                     -- 17
        roster[name].rosterSelection = i;                           -- 18

        atLeastOne = true;
    end

        -- Build Roster for the first time if guild not found.
    if atLeastOne and GRM_G.guildName ~= nil and GRM_G.guildName ~= "" then
        if guildNotFound then
            -- See if it is a Guild NameChange first!
            if GRM.GuildNameChanged ( GRM_G.guildName ) then
                local tempName = GRM.GetStringClassColorByName( GRM_G.addonUser ) .. GRM.SlimName( GRM_G.addonUser ) .. "|r";
                local logEntryWithTime , logEntry = GRM.GetGuildNameChangeString ( tempName , GRM.SlimName ( GRM_G.guildName ) , select ( 2 , GRM.GetTimestamp() ) );

                GRM.PrintLog ( { 15 , logEntry } );   
                GRM.AddLog ( { 15 , logEntryWithTime , tempName , GRM.SlimName ( GRM_G.guildName ) , select ( 2 , GRM.GetTimestamp() ) } ); 
            else
                GRM.Report ( "\n" .. GRM.L ( "Guild Roster Manager" ) .. "\n" .. GRM.L ( "Analyzing guild for the first time..." ) .. "\n" .. GRM.L ( "Building Profiles on ALL \"{name}\" members" , GRM.SlimName ( GRM_G.guildName ) ) .. "\n" );
                -- This reiterates over this, because sometimes it can have a delay. This ensures it is secure.

                local clubID = 0;
                if GRM_G.BuildVersion >= 80000 then
                    clubID = C_Club.GetGuildClubId();
                else
                    clubID = GRM.CreateCustomGUIDValue( GRM_G.guildName );
                end

                if GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ] == nil then
                    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ] = {};
                    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmName"] = GRM_G.guildName;
                    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmCreationDate"] = GRM_G.guildCreationDate or "";
                    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmNumRanks"] = GuildControlGetNumRanks() or 0;
                    GRM_GuildMemberHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmClubID"] = clubID or 0;
                    
                    GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][ GRM_G.guildName ] = {};
                    GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmName"] = GRM_G.guildName;
                    GRM_PlayersThatLeftHistory_Save[ GRM_G.F ][ GRM_G.guildName ]["grmCreationDate"] = GRM_G.guildCreationDate;
                    
                    -- Configure and initialize the backup tables.
                    GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ] = { GRM_G.guildCreationDate };                                       -- Creates a backup index for this guild
                    local type = "Auto";
                    for i = 1 , 2 do
                        if i == 2 then
                            type = "Manual";
                        end
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type] = {};
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type]["date"] = "";
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type]["epochDate"] = 0;
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type]["members"] = {};
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type]["formerMembers"] = {};
                        GRM_GuildDataBackup_Save[ GRM_G.F ][ GRM_G.guildName ][type]["log"] = {};
                    end

                    -- Make sure guild is not already added.
                    if GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] == nil then
                        GRM_PlayerListOfAlts_Save[GRM_G.F][GRM_G.guildName] = {};
                    end

                    -- Log table
                    if GRM_LogReport_Save[ GRM_G.F ][ GRM_G.guildName ] == nil then
                        GRM_LogReport_Save[ GRM_G.F ][ GRM_G.guildName ] = {};
                    end

                    -- Calendar table
                    GRM_CalendarAddQue_Save[ GRM_G.F ][ GRM_G.guildName ] = {};

                    -- Adding properly to alts list for this guild...
                    GRM_G.NeedsToAddSelfToList = true;

                    for player in pairs ( roster ) do
                        -- Build the guild and add each player
                        GRM.AddMemberRecord ( roster[player] , false , nil );
                        if GRM_GuildMemberHistory_Save [ GRM_G.F ][ GRM_G.guildName ][player] ~= nil then
                            GRM_GuildMemberHistory_Save [ GRM_G.F ][ GRM_G.guildName ][player].lastOnline = roster[player].lastOnline;   -- Setting Timestamp for the first time only.
                        end
                    end

                end
            end
        else
            -- This is critical to do in case the guild has added or removed ranks...
            GRM.CheckGuildRanks();

            if not GRM_G.OnFirstLoad then
                C_Timer.After ( 1 , function()
                    GRM_G.ThrottleControlNum = 1;
                    -- new member and leaving members arrays to check at the end - need to reset it here.
                    GRM_G.newPlayers = {};
                    GRM_G.leavingPlayers = {};
                    GRM_G.ScanRosterTimer = 0;
                    GRM.CheckPlayerChanges ( roster );
                end);
            else
                GRM_G.ThrottleControlNum = 1;
                -- new member and leaving members arrays to check at the end - need to reset it here.
                GRM_G.newPlayers = {};
                GRM_G.leavingPlayers = {};
                GRM.CheckPlayerChanges ( roster );
            end
        end
    end
end

